import {
  Admin,
  Resource,
  ListGuesser,
  EditGuesser,
  ShowGuesser,
  TextInput,
  Loading,
  useGetList,
} from "react-admin";
import { Layout } from "./Layout";
import simpleRestProvider from "ra-data-simple-rest";
import CreateGuesser from "./components/create-guesser";
import console from "console";

const dataProvider = simpleRestProvider("/api");

// TO GENERATE: for each resource in the dataProvider, create a Resource component
// Example:
// <Resource
//   name="customers"
//   list={ListGuesser}
//   edit={EditGuesser}
//   show={ShowGuesser}
//   create={CreateGuesser}
// />

export const App = () => (
  <Admin dataProvider={dataProvider} layout={Layout} dashboard={UsersList}>
    <UsersList />
    {/* GENERATED RESOURCES */}

    {/* END GENERATED RESOURCES */}
  </Admin>
);

const UsersList = () => {
  const { data, isPending, error } = useGetList("customers");

  console.log({ data });

  if (isPending) return <Loading />;
  if (error) return <p>Error</p>;
  if (!data) return null;
  return <div>{data.map((user) => user.first_name)}</div>;
};

