import { useGetList, useTheme } from 'react-admin';
import { Card, CardContent, Typography } from '@mui/material';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

interface OrderData {
  id: string;
  created_at: string;
  total: number;
  status: string;
}

interface ChartData {
  date: string;
  amount: number;
  orders: number;
}

const OrdersChart = () => {
  const [theme] = useTheme();
  const { data, isPending, error } = useGetList<OrderData>('orders', {
    pagination: { page: 1, perPage: 50 },
    sort: { field: 'created_at', order: 'ASC' }
  });

  if (isPending) return <Typography>Loading...</Typography>;
  if (error) return <Typography color="error">Error loading orders data</Typography>;

  const chartData: ChartData[] = data?.map(order => ({
    date: new Date(order.created_at).toLocaleDateString(),
    amount: order.total,
    orders: 1
  })) || [];

  return (
    <Card>
      <CardContent>
        <Typography variant="h6" gutterBottom>Orders Over Time</Typography>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke={theme.palette.divider} />
            <XAxis 
              dataKey="date" 
              stroke={theme.palette.text.secondary}
            />
            <YAxis stroke={theme.palette.text.secondary} />
            <Tooltip 
              contentStyle={{
                backgroundColor: theme.palette.background.paper,
                border: `1px solid ${theme.palette.divider}`,
                borderRadius: theme.shape.borderRadius
              }}
            />
            <Legend />
            <Line 
              type="monotone" 
              dataKey="amount" 
              stroke={theme.palette.primary.main} 
              strokeWidth={2}
              name="Order Amount"
            />
          </LineChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

export { OrdersChart };
```

### âœ… BEST PRACTICE: Bar Chart with Data Aggregation
```tsx
import { useGetList, useTheme } from 'react-admin';
import { Card, CardContent, CardHeader, Typography } from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

interface ProductData {
  id: string;
  category: string;
  price: number;
  stock: number;
  name: string;
}

interface CategorySalesData {
  category: string;
  sales: number;
  count: number;
}

const CategorySalesChart = () => {
  const [theme] = useTheme();
  const { data, isPending, error } = useGetList<ProductData>('products', {
    pagination: { page: 1, perPage: 100 }
  });

  if (isPending) return <Typography>Loading...</Typography>;
  if (error) return <Typography color="error">Error loading products data</Typography>;

  // Aggregate data by category
  const chartData: CategorySalesData[] = data?.reduce((acc: CategorySalesData[], product) => {
    const existing = acc.find(item => item.category === product.category);
    const salesValue = product.price * product.stock;

    if (existing) {
      existing.sales += salesValue;
      existing.count += 1;
    } else {
      acc.push({ 
        category: product.category, 
        sales: salesValue,
        count: 1
      });
    }
    return acc;
  }, []) || [];

  return (
    <Card>
      <CardHeader title="Sales by Category" />
      <CardContent>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={chartData}>
            <CartesianGrid 
              strokeDasharray="3 3" 
              stroke={theme.palette.divider} 
            />
            <XAxis 
              dataKey="category" 
              stroke={theme.palette.text.secondary}
            />
            <YAxis stroke={theme.palette.text.secondary} />
            <Tooltip 
              formatter={(value: number) => [`$${value.toLocaleString()}`, 'Sales']}
              contentStyle={{
                backgroundColor: theme.palette.background.paper,
                border: `1px solid ${theme.palette.divider}`,
                borderRadius: theme.shape.borderRadius
              }}
            />
            <Legend />
            <Bar 
              dataKey="sales" 
              fill={theme.palette.primary.main}
              name="Total Sales"
              radius={[4, 4, 0, 0]}
            />
          </BarChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

export { CategorySalesChart };
```

### Dashboard Pie Chart with useDataProvider
```tsx
import { useDataProvider, useNotify } from 'react-admin';
import { Card, CardContent, Typography } from '@mui/material';
import { PieChart, Pie, Cell, Tooltip, ResponsiveContainer } from 'recharts';
import { useEffect, useState } from 'react';

const UserStatusChart = () => {
  const dataProvider = useDataProvider();
  const notify = useNotify();
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    dataProvider.getList('users', { pagination: { page: 1, perPage: 1000 } })
      .then(({ data }) => {
        const statusData = data.reduce((acc, user) => {
          const status = user.active ? 'Active' : 'Inactive';
          const existing = acc.find(item => item.name === status);
          if (existing) {
            existing.value += 1;
          } else {
            acc.push({ name: status, value: 1 });
          }
          return acc;
        }, []);
        setData(statusData);
      })
      .catch(() => notify('Error loading user data', { type: 'warning' }))
      .finally(() => setLoading(false));
  }, [dataProvider, notify]);

  const COLORS = ['#4CAF50', '#F44336'];

  if (loading) return <Typography>Loading...</Typography>;

  return (
    <Card>
      <CardContent>
        <Typography variant="h6" gutterBottom>User Status Distribution</Typography>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie
              data={data}
              dataKey="value"
              nameKey="name"
              cx="50%"
              cy="50%"
              outerRadius={80}
            >
              {data.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip />
          </PieChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};
```

