### âœ… ADVANCED: ComposedChart with Multiple Data Sources
```tsx
import { useGetList, useGetManyReference, useTheme } from 'react-admin';
import { Card, CardContent, Typography } from '@mui/material';
import { ComposedChart, Line, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

interface OrderData {
  id: string;
  created_at: string;
  total: number;
  customer_id: string;
}

const SalesAnalyticsChart = () => {
  const [theme] = useTheme();
  const { data: orders, isPending: ordersLoading, error: ordersError } = useGetList<OrderData>('orders', {
    pagination: { page: 1, perPage: 30 },
    sort: { field: 'created_at', order: 'ASC' }
  });

  if (ordersLoading) return <Typography>Loading...</Typography>;
  if (ordersError) return <Typography color="error">Error loading analytics data</Typography>;

  // Group orders by date and calculate metrics
  const chartData = orders?.reduce((acc: any[], order) => {
    const date = new Date(order.created_at).toLocaleDateString();
    const existing = acc.find(item => item.date === date);

    if (existing) {
      existing.orders += 1;
      existing.revenue += order.total;
      existing.avgOrderValue = existing.revenue / existing.orders;
    } else {
      acc.push({
        date,
        orders: 1,
        revenue: order.total,
        avgOrderValue: order.total
      });
    }
    return acc;
  }, []) || [];

  return (
    <Card>
      <CardContent>
        <Typography variant="h6" gutterBottom>Sales Analytics</Typography>
        <ResponsiveContainer width="100%" height={400}>
          <ComposedChart data={chartData} margin={{ top: 20, right: 30, bottom: 20, left: 20 }}>
            <CartesianGrid strokeDasharray="3 3" stroke={theme.palette.divider} />
            <XAxis 
              dataKey="date" 
              stroke={theme.palette.text.secondary}
            />
            <YAxis 
              yAxisId="left"
              stroke={theme.palette.text.secondary}
            />
            <YAxis 
              yAxisId="right" 
              orientation="right"
              stroke={theme.palette.text.secondary}
            />
            <Tooltip 
              contentStyle={{
                backgroundColor: theme.palette.background.paper,
                border: `1px solid ${theme.palette.divider}`,
                borderRadius: theme.shape.borderRadius
              }}
            />
            <Legend />
            <Bar 
              yAxisId="left"
              dataKey="orders" 
              fill={theme.palette.primary.main}
              name="Number of Orders"
            />
            <Line 
              yAxisId="right"
              type="monotone" 
              dataKey="avgOrderValue" 
              stroke={theme.palette.secondary.main}
              strokeWidth={3}
              name="Avg Order Value ($)"
            />
          </ComposedChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

export { SalesAnalyticsChart };
