import { useGetList, useTheme } from 'react-admin';
import { Card, CardContent, Typography } from '@mui/material';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { CustomerData, GrowthChartData } from './types';

export const CustomerGrowthChart = () => {
  const [theme] = useTheme();
  const { data, isPending, error } = useGetList<CustomerData>('customers', {
    pagination: { page: 1, perPage: 1000 },
    sort: { field: 'subscription_date', order: 'ASC' }
  });

  if (isPending) return <Typography>Loading...</Typography>;
  if (error) return <Typography color="error">Error loading customer growth data</Typography>;

  const chartData: GrowthChartData[] = data?.reduce((acc: GrowthChartData[], customer, index) => {
    const date = new Date(customer.subscription_date);
    const monthYear = date.toLocaleDateString('en-US', { year: 'numeric', month: 'short' });
    const existing = acc.find(item => item.date === monthYear);

    if (existing) {
      existing.customers += 1;
      existing.cumulative = index + 1;
    } else {
      acc.push({ 
        date: monthYear, 
        customers: 1,
        cumulative: index + 1
      });
    }
    return acc;
  }, []) || [];

  return (
    <Card>
      <CardContent>
        <Typography variant="h6" gutterBottom>Customer Growth Over Time</Typography>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke={theme.palette.divider} />
            <XAxis 
              dataKey="date" 
              stroke={theme.palette.text.secondary}
            />
            <YAxis stroke={theme.palette.text.secondary} />
            <Tooltip 
              contentStyle={{
                backgroundColor: theme.palette.background.paper,
                border: `1px solid ${theme.palette.divider}`,
                borderRadius: theme.shape.borderRadius
              }}
            />
            <Legend />
            <Line 
              type="monotone" 
              dataKey="customers" 
              stroke={theme.palette.primary.main} 
              strokeWidth={2}
              name="New Customers"
            />
            <Line 
              type="monotone" 
              dataKey="cumulative" 
              stroke={theme.palette.secondary.main} 
              strokeWidth={2}
              name="Total Customers"
            />
          </LineChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

