import { useGetList, useTheme } from 'react-admin';
import { Card, CardContent, Typography } from '@mui/material';
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { CustomerData, CompanyChartData } from './types';

export const CustomersByCompanyChart = () => {
  const [theme] = useTheme();
  const { data, isPending, error } = useGetList<CustomerData>('customers', {
    pagination: { page: 1, perPage: 1000 }
  });

  if (isPending) return <Typography>Loading...</Typography>;
  if (error) return <Typography color="error">Error loading company data</Typography>;

  const companyData = data?.reduce((acc: { [key: string]: number }, customer) => {
    const company = customer.company || 'Unknown';
    acc[company] = (acc[company] || 0) + 1;
    return acc;
  }, {}) || {};

  const totalCustomers = Object.values(companyData).reduce((sum, count) => sum + count, 0);

  const chartData: CompanyChartData[] = Object.entries(companyData)
    .map(([company, customers]) => ({
      company,
      customers,
      percentage: Math.round((customers / totalCustomers) * 100)
    }))
    .sort((a, b) => b.customers - a.customers)
    .slice(0, 8); // Show top 8 companies

  const COLORS = [
    theme.palette.primary.main,
    theme.palette.secondary.main,
    theme.palette.error.main,
    theme.palette.warning.main,
    theme.palette.info.main,
    theme.palette.success.main,
    theme.palette.primary.light,
    theme.palette.secondary.light
  ];

  return (
    <Card>
      <CardContent>
        <Typography variant="h6" gutterBottom>Top Companies by Customer Count</Typography>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie
              data={chartData}
              dataKey="customers"
              nameKey="company"
              cx="50%"
              cy="50%"
              outerRadius={80}
              label={({ company, percentage }) => `${company}: ${percentage}%`}
              labelLine={false}
            >
              {chartData.map((entry, index) => (
                <Cell 
                  key={`cell-${index}`} 
                  fill={COLORS[index % COLORS.length]} 
                />
              ))}
            </Pie>
            <Tooltip 
              formatter={(value: number, name: string) => [value, 'Customers']}
              contentStyle={{
                backgroundColor: theme.palette.background.paper,
                border: `1px solid ${theme.palette.divider}`,
                borderRadius: theme.shape.borderRadius
              }}
            />
            <Legend />
          </PieChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

