{
  "name": "data-apps-template",
  "version": "1.0.0",
  "description": "A data application template with FastAPI backend and React Admin frontend for Databricks Apps",
  "private": true,
  "scripts": {
    "setup": "cd frontend/reactadmin && npm install",
    "dev": "concurrently \"cd frontend/reactadmin && npm run dev\" \"cd backend && uv run uvicorn main:app --reload --host 0.0.0.0 --port 8000\"",
    "dev:frontend": "cd frontend/reactadmin && npm run dev",
    "dev:backend": "cd backend && uv run uvicorn main:app --reload --host 0.0.0.0 --port 8000",
    "build": "cd frontend/reactadmin && npm install && npm run build"
  },
  "devDependencies": {
    "concurrently": "^8.2.2"
  }
}

import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";

// https://vitejs.dev/config/
export default defineConfig(({ mode }) => ({
  plugins: [react()],
  server: {
    host: true,
    port: 3000,
    proxy: {
      "/api": {
        target: "http://localhost:8000",
        changeOrigin: true,
        secure: false,
      },
    },
  },
  build: {
    sourcemap: mode === "development",
    outDir: "dist",
    assetsDir: "assets",
  },
  base: "/",
}));

