// <Resource
//   name="customers"
//   list={ListGuesser}
//   edit={EditGuesser}
//   show={ShowGuesser}
//   create={CreateGuesser}
// />

export const App = () => (
  <Admin dataProvider={dataProvider} layout={Layout}>
    {/* GENERATED RESOURCES */}
    hey
    <Resource
      name="customers"
      list={() => (
        <ListGuesser
          filters={[
            <TextInput label="Search" source="q" alwaysOn key="search" />,
          ]}
        />
      )}
      edit={EditGuesser}
      show={ShowGuesser}
      create={CreateGuesser}
    />
    {/* END GENERATED RESOURCES */}
  </Admin>
);

