import { Input, Label } from '@appdotbuild/design';
import { Link2, Key } from 'lucide-react';
import { cn } from '@appdotbuild/design';

type DatabricksConfig = {
  orgDetail1: string;
  orgDetail2: string;
};

type DatabricksConfigFormProps = {
  config: DatabricksConfig;
  onConfigChange: (config: DatabricksConfig) => void;
  className?: string;
};

export function DatabricksConfigForm({
  config,
  onConfigChange,
  className,
}: DatabricksConfigFormProps) {
  const handleOrgDetail1Change = (e: React.ChangeEvent<HTMLInputElement>) => {
    onConfigChange({
      ...config,
      orgDetail1: e.target.value,
    });
  };

  const handleOrgDetail2Change = (e: React.ChangeEvent<HTMLInputElement>) => {
    onConfigChange({
      ...config,
      orgDetail2: e.target.value,
    });
  };

  return (
    <div className={cn('space-y-6', className)}>
      <div className="space-y-2">
        <div className="flex items-center gap-2">
          <Link2 className="w-4 h-4 text-muted-foreground" />
          <Label htmlFor="workspace-url" className="text-sm font-medium">
            Workspace URL
          </Label>
        </div>
        <Input
          id="workspace-url"
          type="url"
          placeholder="https://workspace.cloud.databricks.com"
          value={config.orgDetail1}
          onChange={handleOrgDetail1Change}
        />
        <p className="text-xs text-muted-foreground">
          Your Databricks workspace URL
        </p>
      </div>

      <div className="space-y-2">
        <div className="flex items-center gap-2">
          <Key className="w-4 h-4 text-muted-foreground" />
          <Label htmlFor="access-token" className="text-sm font-medium">
            Access Token
          </Label>
        </div>
        <Input
          id="access-token"
          type="password"
          placeholder="dapi"
          value={config.orgDetail2}
          onChange={handleOrgDetail2Change}
        />
        <p className="text-xs text-muted-foreground">
          Generate from user settings  Access tokens
        </p>
      </div>
    </div>
  );
}

