type DeploymentTargetSelectorProps = {
  selectedTarget: DeploymentTarget;
  onTargetChange: (target: DeploymentTarget) => void;
  databricksConfig: DatabricksConfig;
  onDatabricksConfigChange: (config: DatabricksConfig) => void;
  className?: string;
};

export function DeploymentTargetSelector({
  selectedTarget,
  onTargetChange,
  databricksConfig,
  onDatabricksConfigChange,
  className,
}: DeploymentTargetSelectorProps) {
  const isStaff = useIsStaff();

  const formMethods = useForm<DeploymentFormData>({
    resolver: zodResolver(deploymentFormSchema),
    defaultValues: {
      selectedTarget,
      databricksConfig,
    },
  });

  const { watch, setValue } = formMethods;
  const watchedTarget = watch('selectedTarget');
  const watchedConfig = watch('databricksConfig');

  // Sync form state with parent props
  useEffect(() => {
    setValue('selectedTarget', selectedTarget);
  }, [selectedTarget, setValue]);

  useEffect(() => {
    setValue('databricksConfig', databricksConfig);
  }, [databricksConfig, setValue]);

  // Notify parent of form changes
  useEffect(() => {
    if (watchedTarget !== selectedTarget) {
      onTargetChange(watchedTarget);
    }
  }, [watchedTarget, selectedTarget, onTargetChange]);

  useEffect(() => {
    if (JSON.stringify(watchedConfig) !== JSON.stringify(databricksConfig)) {
      onDatabricksConfigChange(watchedConfig);
    }
  }, [watchedConfig, databricksConfig, onDatabricksConfigChange]);

