export function DeploymentTargetSelector({
  selectedTarget,
  onTargetChange,
  databricksConfig,
  onDatabricksConfigChange,
  className,
}: DeploymentTargetSelectorProps) {
  const isStaff = useIsStaff();

  // Don't render for non-staff users
  if (!isStaff) {
    return null;
  }

  return (
    <div className={cn('space-y-4', className)}>
      <div className="flex items-center gap-2">
        <h3 className="text-sm font-semibold">Deployment Platform</h3>
        <Badge variant="outline" className="text-xs">
          Staff Only
        </Badge>
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        {/* Koyeb Card */}
        <Card
          className={cn(
            'relative cursor-pointer transition-all duration-200 hover:bg-muted/50',
            selectedTarget === 'koyeb'
              ? 'border-2 border-primary bg-primary/10'
              : 'border-2 border-transparent',
