import { useEffect, useState } from 'react';
import { ChatInput } from '~/components/chat/chat-input';
import { ChatMessageLimit } from '~/components/chat/chat-message-limit';
import { ChatList } from '~/components/chat/list/chat-list';
import { DeploymentTargetSelector } from '~/components/chat/deployment/deployment-target-selector';
import { DecoratedInputContainer } from '~/components/shared/decorations';
import { HeroTitle } from '~/components/shared/title';
import { AnalyticsEvents, sendPageView } from '~/external/segment';
import { useCurrentApp } from '~/hooks/useCurrentApp';
import { messagesStore } from '~/stores/messages-store';
import type {
  DeploymentTarget,
  DatabricksConfig,
} from '~/components/chat/deployment/deployment-target-selector';

export function AuthenticatedHome() {
  const clearCurrentApp = useCurrentApp((state) => state.clearCurrentApp);
  const [deploymentTarget, setDeploymentTarget] =
    useState<DeploymentTarget>('koyeb');
  const [databricksConfig, setDatabricksConfig] = useState<DatabricksConfig>({
    orgDetail1: '',
    orgDetail2: '',
  });

  const handleTargetChange = (target: DeploymentTarget) => {
    setDeploymentTarget(target);
  };

  const handleDatabricksConfigChange = (config: DatabricksConfig) => {
    setDatabricksConfig(config);
  };

  const getDeploymentConfig = () => {
    if (deploymentTarget === 'databricks') {
      return {
        target: 'databricks' as const,
        databricks: databricksConfig,
      };
    }
    return { target: 'koyeb' as const };
  };

  const isDeploymentReady = () => {
    if (deploymentTarget === 'databricks') {
      return !!(databricksConfig.orgDetail1 && databricksConfig.orgDetail2);
    }
    return true;
  };

  useEffect(() => {
    sendPageView(AnalyticsEvents.PAGE_VIEW_HOME);
  }, []);

  // clean up the current app state
  useEffect(() => {
    clearCurrentApp();
    messagesStore.clearMessages('new');
  }, [clearCurrentApp]);

  return (
    <section className="hero relative grow overflow-x-hidden overflow-y-auto">
      <div
        data-testid="authenticated-home"
        className="w-full h-full flex flex-col gap-8 lg:gap-12 pt-20 md:pt-24 lg:pt-48 xl:pt-56 items-center"
      >
        <HeroTitle>
          An open-source <br className="block md:hidden xl:block" />
          AI agent that builds <br className="block md:hidden xl:block" />
          full-stack apps
        </HeroTitle>

        {/* Input Area with Optional Deployment Configuration */}
        <div className="w-full max-w-2xl px-4 lg:px-6 space-y-8">
          {/* Subtle deployment selector for staff */}
          <DeploymentTargetSelector
            selectedTarget={deploymentTarget}
            onTargetChange={handleTargetChange}
            databricksConfig={databricksConfig}
            onDatabricksConfigChange={handleDatabricksConfigChange}
          />

          <DecoratedInputContainer>
            <ChatInput
              deploymentTarget={deploymentTarget}
              databricksConfig={databricksConfig}
              getDeploymentConfig={getDeploymentConfig}
              isDeploymentReady={isDeploymentReady}
            />
            <div className="absolute left-0 right-0 top-full mt-2">
              <ChatMessageLimit />
            </div>
          </DecoratedInputContainer>
        </div>
        <ChatList />
      </div>
    </section>
  );
}

