import {
  Badge,
  Card,
  CardContent,
  CardHeader,
  CardTitle,
  CardDescription,
} from '@appdotbuild/design';
import { Server, Database } from 'lucide-react';
import { DatabricksConfigForm } from './databricks-config-form';
import { cn } from '@appdotbuild/design';
import { useForm, FormProvider } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { useIsStaff } from '~/hooks/use-is-staff';
import { forwardRef, useImperativeHandle } from 'react';

// Zod schema for validation - single source of truth
const deploymentFormSchema = z.object({
  selectedTarget: z.enum(['koyeb', 'databricks']),
  databricksConfig: z.object({
    hostUrl: z.string().min(1, 'Workspace URL is required'),
    personalAccessToken: z.string().min(1, 'Access token is required'),
  }),
});

// Derived types from Zod schemas
export type DeploymentFormData = z.infer<typeof deploymentFormSchema>;
export type DeploymentTarget = DeploymentFormData['selectedTarget'];
export type DatabricksConfig = DeploymentFormData['databricksConfig'];

// Deployment configuration types
export type DeploymentConfig =
  | {
      target: 'koyeb';
    }
  | {
      target: 'databricks';
      databricks: DatabricksConfig;
    };

// Imperative handle interface
export interface DeploymentTargetSelectorHandle {
  getDeploymentConfig: () => DeploymentConfig;
  isDeploymentReady: () => boolean;
}

type DeploymentTargetSelectorProps = {
  className?: string;
};

export const DeploymentTargetSelector = forwardRef<
  DeploymentTargetSelectorHandle,
  DeploymentTargetSelectorProps
>(({ className }, ref) => {
  const isStaff = useIsStaff();

  const formMethods = useForm<DeploymentFormData>({
    resolver: zodResolver(deploymentFormSchema),
    defaultValues: {
      selectedTarget,
      databricksConfig,
    },
  });

  const { watch, setValue } = formMethods;
  const watchedTarget = watch('selectedTarget');
  const watchedConfig = watch('databricksConfig');

  // Sync form state with parent props
  useEffect(() => {
    setValue('selectedTarget', selectedTarget);
  }, [selectedTarget, setValue]);

  useEffect(() => {
    setValue('databricksConfig', databricksConfig);
  }, [databricksConfig, setValue]);

  // Notify parent of form changes
  useEffect(() => {
    if (watchedTarget !== selectedTarget) {
      onTargetChange(watchedTarget);
    }
  }, [watchedTarget, selectedTarget, onTargetChange]);

  useEffect(() => {
    if (JSON.stringify(watchedConfig) !== JSON.stringify(databricksConfig)) {
      onDatabricksConfigChange(watchedConfig);
    }
  }, [watchedConfig, databricksConfig, onDatabricksConfigChange]);

  const handleTargetClick = (target: DeploymentTarget) => {
    setValue('selectedTarget', target);
  };

  // Don't render for non-staff users
  if (!isStaff) {
    return null;
  }

  return (
    <FormProvider {...formMethods}>
      <div className={cn('space-y-4', className)}>
        <div className="flex items-center gap-2">
          <h3 className="text-sm font-semibold">Deployment Platform</h3>
          <Badge variant="outline" className="text-xs">
            Staff Only
          </Badge>
        </div>

        <div className="grid gap-4 md:grid-cols-2">
          {/* Koyeb Card */}
          <Card
            className={cn(
              'relative cursor-pointer transition-all duration-200 hover:bg-muted/50',
              watchedTarget === 'koyeb'
                ? 'border-2 border-primary bg-primary/10'
                : 'border-2 border-transparent',
            )}
            onClick={() => handleTargetClick('koyeb')}
          >
            <CardContent className="p-6">
              <div className="flex items-center gap-4">
                <div className="flex-shrink-0 p-3 bg-muted/50 rounded-lg">
                  <Server
                    className={cn(
                      'w-6 h-6 transition-colors',
                      watchedTarget === 'koyeb'
                        ? 'text-primary'
                        : 'text-muted-foreground',
                    )}
                  />
                </div>
                <div className="flex-1">
                  <h4 className="font-semibold text-base mb-1">Koyeb</h4>
                  <p className="text-sm text-muted-foreground">
                    Default Cloud Platform
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Databricks Card */}
          <Card
            className={cn(
              'relative cursor-pointer transition-all duration-200 hover:bg-muted/50',
              watchedTarget === 'databricks'
                ? 'border-2 border-primary bg-primary/10'
                : 'border-2 border-transparent',
            )}
            onClick={() => handleTargetClick('databricks')}
          >
            <CardContent className="p-6">
              <div className="flex items-center gap-4">
                <div className="flex-shrink-0 p-3 bg-muted/50 rounded-lg">
                  <Database
                    className={cn(
                      'w-6 h-6 transition-colors',
                      watchedTarget === 'databricks'
                        ? 'text-primary'
                        : 'text-muted-foreground',
                    )}
                  />
                </div>
                <div className="flex-1">
                  <div className="flex items-center gap-2 mb-1">
                    <h4 className="font-semibold text-base">Databricks Apps</h4>
                  </div>
                  <p className="text-sm text-muted-foreground">
                    Enterprise Platform
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {watchedTarget === 'databricks' && (
          <Card className="animate-in slide-in-from-top-2 duration-300">
            <CardHeader className="pb-4">
              <CardTitle className="text-lg">Workspace Configuration</CardTitle>
              <CardDescription>
                Connect your Databricks workspace to deploy apps
              </CardDescription>
            </CardHeader>
            <CardContent>
              <DatabricksConfigForm />
            </CardContent>
          </Card>
        )}

        <div className="text-center">
          <p className="text-xs text-muted-foreground">
            {watchedTarget === 'koyeb'
              ? 'Fast, reliable deployment to our managed cloud infrastructure'
              : 'Deploy to Databricks Apps. Currently supports NiceGUI applications only.'}
          </p>
        </div>
      </div>
    </FormProvider>
  );
}

