1:import { ReactNode } from 'react';
2:import { Form, type FormProps, Translate, WithRecord } from 'ra-core';
3:import { useNavigate } from 'react-router';
4:import { CircleX, Save } from 'lucide-react';
5:import { cn } from '@appdotbuild/design';
6:import { Button } from '@appdotbuild/design';
7:import { DeleteButton } from '@/components/admin/delete-button';
9:export const SimpleForm = ({
10:  children,
11:  className,
12:  toolbar = defaultFormToolbar,
13:  ...rest
14:}: {
15:  children: ReactNode;
16:  className?: string;
17:  toolbar?: ReactNode;
18:} & FormProps) => {
19:  return (
20:    <Form
21:      className={cn(`flex flex-col gap-4 w-full max-w-lg`, className)}
22:      {...rest}
23:    >
24:      {children}
25:      {toolbar}
26:    </Form>
27:  );
28:};
30:export const FormToolbar = ({ className }: { className?: string }) => {
31:  const navigate = useNavigate();
32:  return (
33:    <div
34:      className={cn(
35:        'sticky pt-4 pb-4 md:block md:pt-0 md:pb-0 bottom-0 bg-linear-to-b from-transparent to-background to-10%',
36:        className,
37:      )}
38:    >
39:      <div className="flex flex-row gap-4 justify-start">
40:        <Button type="submit">
41:          <Save />
42:          <Translate i18nKey="ra.action.save">Save</Translate>
43:        </Button>
44:        <Button
45:          type="button"
46:          variant="outline"
47:          onClick={() => void navigate(-1)}
48:        >
49:          <CircleX />
50:          <Translate i18nKey="ra.action.cancel">Cancel</Translate>
51:        </Button>
52:        <div className="flex-1" />
53:        <WithRecord
54:          render={(record) => record.id !== null && <DeleteButton />}
55:        />
56:      </div>
57:    </div>
58:  );
59:};
61:const defaultFormToolbar = <FormToolbar />;

