      data: convertAppToRecord(response.data),
    };
  },

  deleteMany: async (params: DeleteManyParams): Promise<DeleteManyResult> => {
    // Use admin endpoint for bulk delete
    await Promise.all(
      params.ids.map((id) => apiClient.delete(`/admin/apps/${id}`)),
    );

    return {
      data: params.ids,
    };
  },

  // ... rest of methods
}
