     1â†’import { and, eq, isNull } from 'drizzle-orm';
     2â†’import type { FastifyReply, FastifyRequest } from 'fastify';
     3â†’import { validate } from 'uuid';
     4â†’import { apps, appPrompts, deployments, db } from '../db';
     5â†’import {
     6â†’  deleteKoyebService,
     7â†’  deleteKoyebDomain,
     8â†’  deleteKoyebApp,
     9â†’  deleteKoyebOrganization,
    10â†’  getOrganizationToken,
    11â†’} from '../deploy/koyeb';
    12â†’import { cleanupDatabricksResources } from '../deploy/databricks';
    13â†’import { deleteNeonProject } from '../deploy/neon';
    14â†’import { deleteECRImages, deleteECRRepository } from '../ecr';
    15â†’import { DEFAULT_OWNER, GithubEntity } from '../github/entity';
    16â†’import { archiveRepository } from '../github/delete-repository';
    17â†’import { logger } from '../logger';
    18â†’import { Instrumentation } from '../instrumentation';
    19â†’
    20â†’export async function deleteApp(
    21â†’  request: FastifyRequest,
    22â†’  reply: FastifyReply,
    23â†’): Promise<void> {
    24â†’  const user = request.user;
    25â†’  const { id } = request.params as { id: string };
    26â†’
    27â†’  if (!validate(id)) {
    28â†’    return reply.status(400).send({ error: 'Invalid app ID' });
    29â†’  }
    30â†’
    31â†’  try {
    32â†’    const app = await db
    33â†’      .select({
    34â†’        id: apps.id,
    35â†’        name: apps.name,
    36â†’        ownerId: apps.ownerId,
    37â†’        koyebAppId: apps.koyebAppId,
    38â†’        koyebServiceId: apps.koyebServiceId,
    39â†’        koyebDomainId: apps.koyebDomainId,
    40â†’        neonProjectId: apps.neonProjectId,
    41â†’        githubUsername: apps.githubUsername,
    42â†’        repositoryUrl: apps.repositoryUrl,
    43â†’        databricksHost: apps.databricksHost,
    44â†’        databricksApiKey: apps.databricksApiKey,
    45â†’        deletedAt: apps.deletedAt,
    46â†’      })
    47â†’      .from(apps)
    48â†’      .where(and(eq(apps.id, id), eq(apps.ownerId, user.id)));
    49â†’
    50â†’    if (!app || app.length === 0) {
    51â†’      return reply.status(404).send({
    52â†’        error: 'App not found',
    53â†’      });
    54â†’    }
    55â†’
    56â†’    const appData = app[0]!;
    57â†’
    58â†’    // Check if app is already deleted
    59â†’    if (appData.deletedAt) {
    60â†’      return reply.status(400).send({
    61â†’        error: 'App is already deleted',
    62â†’      });
    63â†’    }
    64â†’
    65â†’    // Get organization info and check remaining deployments (for Koyeb cleanup decisions)
    66â†’    const userDeployments = await db
    67â†’      .select({
    68â†’        koyebOrgId: deployments.koyebOrgId,
    69â†’        appId: deployments.appId,
    70â†’      })
    71â†’      .from(deployments)
    72â†’      .where(
    73â†’        and(eq(deployments.ownerId, user.id), isNull(deployments.deletedAt)),
    74â†’      );
    75â†’
    76â†’    const koyebOrgId = userDeployments[0]?.koyebOrgId;
    77â†’    const remainingDeployments = userDeployments.filter((d) => d.appId !== id);
    78â†’    const isLastActiveDeployment = remainingDeployments.length === 0;
    79â†’
    80â†’    await db.delete(appPrompts).where(eq(appPrompts.appId, id));
    81â†’    logger.info('Hard deleted app prompts', { appId: id });
    82â†’
    83â†’    await db
    84â†’      .update(deployments)
    85â†’      .set({ deletedAt: new Date() })
    86â†’      .where(eq(deployments.appId, id));
    87â†’    logger.info('Soft deleted deployment records', { appId: id });
    88â†’
    89â†’    // Phase 2: Soft delete the main app record (preserve for analytics)
    90â†’    await db
    91â†’      .update(apps)
    92â†’      .set({
    93â†’        deletedAt: new Date(),
    94â†’        updatedAt: new Date(),
    95â†’        koyebAppId: null,
    96â†’        koyebServiceId: null,
    97â†’        koyebDomainId: null,
    98â†’        neonProjectId: null,
    99â†’      })
   100â†’      .where(eq(apps.id, id));
   101â†’    logger.info('Soft deleted main app record', { appId: id });
   102â†’
   103â†’    // Databricks resource cleanup
   104â†’    if (appData.databricksHost && appData.databricksApiKey) {
   105â†’      try {
   106â†’        await cleanupDatabricksResources({
   107â†’          appId: id,
   108â†’          databricksHost: appData.databricksHost,
   109â†’          databricksApiKey: appData.databricksApiKey,
   110â†’        });
   111â†’        logger.info('Databricks resource cleanup completed', { appId: id });
   112â†’      } catch (error) {
   113â†’        logger.error('Failed to cleanup Databricks resources', {
   114â†’          appId: id,
   115â†’          error: error instanceof Error ? error.message : String(error),
   116â†’        });
   117â†’      }
   118â†’    } else {
   119â†’      try {
   120â†’        await cleanupKoyebResources({
   121â†’          appId: id,
   122â†’          ownerId: user.id,
   123â†’          koyebServiceId: appData.koyebServiceId,
   124â†’          koyebDomainId: appData.koyebDomainId,
   125â†’          koyebAppId: appData.koyebAppId,
   126â†’          koyebOrgId: koyebOrgId,
   127â†’          isLastActiveDeployment: isLastActiveDeployment,
   128â†’        });
   129â†’      } catch (error) {
   130â†’        logger.error('Failed to cleanup Koyeb resources', {
   131â†’          appId: id,
   132â†’          error: error instanceof Error ? error.message : String(error),
   133â†’        });
   134â†’      }
   135â†’    }
   136â†’
   137â†’    await cleanupNeonResources({
   138â†’      appId: id,
   139â†’      neonProjectId: appData.neonProjectId,
   140â†’    });
   141â†’
   142â†’    await cleanupECRResources({
   143â†’      appId: id,
   144â†’      githubUsername: appData.githubUsername,
   145â†’    });
   146â†’
   147â†’    await cleanupGithubResources({
   148â†’      appId: id,
   149â†’      repositoryUrl: appData.repositoryUrl,
   150â†’      githubUsername: appData.githubUsername,
   151â†’      githubAccessToken: user.githubAccessToken,
   152â†’    });
   153â†’
   154â†’    return reply.status(200).send({
   155â†’      message: 'App deleted successfully',
   156â†’      appId: id,
   157â†’    });
   158â†’  } catch (error) {
   159â†’    logger.error('Error deleting app', {
   160â†’      appId: id,
   161â†’      userId: user.id,
   162â†’      error: error instanceof Error ? error.message : error,
   163â†’    });
   164â†’
   165â†’    Instrumentation.captureError(error as Error, {
   166â†’      context: 'delete_app_main',
   167â†’      appId: id,
   168â†’      userId: user.id,
   169â†’    });
   170â†’
   171â†’    return reply.status(500).send({
   172â†’      error: 'Internal server error while deleting app',
   173â†’    });
   174â†’  }
   175â†’}
