   177â†’async function cleanupKoyebResources({
   178â†’  appId,
   179â†’  ownerId,
   180â†’  koyebServiceId,
   181â†’  koyebDomainId,
   182â†’  koyebAppId,
   183â†’  koyebOrgId,
   184â†’  isLastActiveDeployment,
   185â†’}: {
   186â†’  appId: string;
   187â†’  ownerId: string;
   188â†’  koyebServiceId: string | null;
   189â†’  koyebDomainId: string | null;
   190â†’  koyebAppId: string | null;
   191â†’  koyebOrgId: string | null | undefined;
   192â†’  isLastActiveDeployment: boolean;
   193â†’}) {
   194â†’  // Skip if no Koyeb resources to clean up
   195â†’  if (!koyebServiceId && !koyebDomainId && !koyebAppId) {
   196â†’    logger.info('No Koyeb resources to clean up', { appId });
   197â†’    return;
   198â†’  }
   199â†’
   200â†’  try {
   201â†’    if (!koyebOrgId) {
   202â†’      logger.warn('No Koyeb organization found for user, skipping cleanup', {
   203â†’        appId,
   204â†’        ownerId,
   205â†’      });
   206â†’      return;
   207â†’    }
   208â†’
   209â†’    const userToken = await getOrganizationToken(koyebOrgId);
   210â†’
   211â†’    logger.info('Starting Koyeb resource cleanup', {
   212â†’      appId,
   213â†’      hasService: !!koyebServiceId,
   214â†’      hasDomain: !!koyebDomainId,
   215â†’      hasApp: !!koyebAppId,
   216â†’    });
   217â†’
   218â†’    if (koyebServiceId) {
   219â†’      try {
   220â†’        await deleteKoyebService({
   221â†’          serviceId: koyebServiceId,
   222â†’          token: userToken,
   223â†’        });
   224â†’        logger.info('Deleted Koyeb service', {
   225â†’          appId,
   226â†’          serviceId: koyebServiceId,
   227â†’        });
   228â†’      } catch (error) {
   229â†’        logger.error('Failed to delete Koyeb service (continuing)', {
   230â†’          appId,
   231â†’          serviceId: koyebServiceId,
   232â†’          error: error instanceof Error ? error.message : error,
   233â†’        });
   234â†’
   235â†’        Instrumentation.captureError(error as Error, {
   236â†’          context: 'delete_koyeb_service',
   237â†’          appId,
   238â†’          serviceId: koyebServiceId,
   239â†’        });
   240â†’      }
   241â†’    }
   242â†’
   243â†’    if (koyebDomainId) {
   244â†’      try {
   245â†’        await deleteKoyebDomain({
   246â†’          domainId: koyebDomainId,
   247â†’          token: userToken,
   248â†’        });
   249â†’        logger.info('Deleted Koyeb domain', {
   250â†’          appId,
   251â†’          domainId: koyebDomainId,
   252â†’        });
   253â†’      } catch (error) {
   254â†’        logger.error('Failed to delete Koyeb domain (continuing)', {
   255â†’          appId,
   256â†’          domainId: koyebDomainId,
   257â†’          error: error instanceof Error ? error.message : error,
   258â†’        });
   259â†’
   260â†’        Instrumentation.captureError(error as Error, {
   261â†’          context: 'delete_koyeb_domain',
   262â†’          appId,
   263â†’          domainId: koyebDomainId,
   264â†’        });
   265â†’      }
   266â†’    }
   267â†’
   268â†’    if (koyebAppId) {
   269â†’      try {
   270â†’        await deleteKoyebApp({
   271â†’          appId: koyebAppId,
   272â†’          token: userToken,
   273â†’        });
   274â†’        logger.info('Deleted Koyeb app', {
   275â†’          appId,
   276â†’          koyebAppId: koyebAppId,
   277â†’        });
   278â†’      } catch (error) {
   279â†’        logger.error('Failed to delete Koyeb app (continuing)', {
   280â†’          appId,
   281â†’          koyebAppId: koyebAppId,
   282â†’          error: error instanceof Error ? error.message : error,
   283â†’        });
   284â†’
   285â†’        Instrumentation.captureError(error as Error, {
   286â†’          context: 'delete_koyeb_app',
   287â†’          appId,
   288â†’          koyebAppId: koyebAppId,
   289â†’        });
   290â†’      }
   291â†’    }
   292â†’
   293â†’    if (isLastActiveDeployment && koyebOrgId) {
   294â†’      logger.info(
   295â†’        'Initiating async Koyeb organization deletion (last active deployment)',
   296â†’        {
   297â†’          appId,
   298â†’          koyebOrgId,
   299â†’          ownerId,
   300â†’        },
   301â†’      );
   302â†’
   303â†’      deleteKoyebOrganization({
   304â†’        orgId: koyebOrgId,
   305â†’        token: userToken,
   306â†’      }).catch((error) => {
   307â†’        logger.error('Async Koyeb organization deletion failed', {
   308â†’          appId,
   309â†’          koyebOrgId,
   310â†’          error: error instanceof Error ? error.message : error,
   311â†’        });
   312â†’        Instrumentation.captureError(error as Error, {
   313â†’          context: 'async_delete_koyeb_organization',
   314â†’          appId,
   315â†’          koyebOrgId,
   316â†’        });
   317â†’      });
   318â†’    } else {
   319â†’      logger.info('Keeping Koyeb organization (user has other deployments)', {
   320â†’        appId,
   321â†’        koyebOrgId,
   322â†’        isLastActiveDeployment,
   323â†’      });
   324â†’    }
   325â†’
   326â†’    logger.info('Koyeb resource cleanup completed', { appId });
   327â†’  } catch (error) {
   328â†’    logger.error('Failed to clean up Koyeb resources', {
   329â†’      appId,
   330â†’      ownerId,
   331â†’      error: error instanceof Error ? error.message : error,
   332â†’    });
   333â†’
   334â†’    Instrumentation.captureError(error as Error, {
   335â†’      context: 'cleanup_koyeb_resources',
   336â†’      appId,
   337â†’      ownerId,
   338â†’    });
   339â†’  }
   340â†’}
