   342â†’async function cleanupNeonResources({
   343â†’  appId,
   344â†’  neonProjectId,
   345â†’}: {
   346â†’  appId: string;
   347â†’  neonProjectId: string | null;
   348â†’}) {
   349â†’  if (!neonProjectId) {
   350â†’    logger.info('No Neon project to clean up', { appId });
   351â†’    return;
   352â†’  }
   353â†’
   354â†’  try {
   355â†’    logger.info('Starting Neon project cleanup', {
   356â†’      appId,
   357â†’      neonProjectId,
   358â†’    });
   359â†’
   360â†’    await deleteNeonProject({
   361â†’      projectId: neonProjectId,
   362â†’    });
   363â†’
   364â†’    logger.info('Neon project cleanup completed', {
   365â†’      appId,
   366â†’      neonProjectId,
   367â†’    });
   368â†’  } catch (error) {
   369â†’    logger.error('Failed to clean up Neon project', {
   370â†’      appId,
   371â†’      neonProjectId,
   372â†’      error: error instanceof Error ? error.message : error,
   373â†’    });
   374â†’
   375â†’    Instrumentation.captureError(error as Error, {
   376â†’      context: 'cleanup_neon_resources',
   377â†’      appId,
   378â†’      neonProjectId,
   379â†’    });
   380â†’  }
   381â†’}
