   383â†’async function cleanupECRResources({
   384â†’  appId,
   385â†’  githubUsername,
   386â†’}: {
   387â†’  appId: string;
   388â†’  githubUsername: string | null;
   389â†’}) {
   390â†’  if (!githubUsername) {
   391â†’    logger.info('No GitHub username available, skipping ECR cleanup', {
   392â†’      appId,
   393â†’    });
   394â†’    return;
   395â†’  }
   396â†’
   397â†’  try {
   398â†’    logger.info('Starting ECR resources cleanup', {
   399â†’      appId,
   400â†’      githubUsername,
   401â†’    });
   402â†’
   403â†’    await deleteECRImages({
   404â†’      appId,
   405â†’      githubUsername,
   406â†’    });
   407â†’
   408â†’    await deleteECRRepository({
   409â†’      appId,
   410â†’      githubUsername,
   411â†’    });
   412â†’
   413â†’    logger.info('ECR resources cleanup completed', {
   414â†’      appId,
   415â†’      githubUsername,
   416â†’    });
   417â†’  } catch (error) {
   418â†’    logger.error('Failed to clean up ECR resources', {
   419â†’      appId,
   420â†’      githubUsername,
   421â†’      error: error instanceof Error ? error.message : error,
   422â†’    });
   423â†’
   424â†’    Instrumentation.captureError(error as Error, {
   425â†’      context: 'cleanup_ecr_resources',
   426â†’      appId,
   427â†’      githubUsername,
   428â†’    });
   429â†’  }
   430â†’}
   431â†’
   432â†’async function cleanupGithubResources({
   433â†’  appId,
   434â†’  repositoryUrl,
   435â†’  githubUsername,
   436â†’  githubAccessToken,
   437â†’}: {
   438â†’  appId: string;
   439â†’  repositoryUrl: string | null;
   440â†’  githubUsername: string | null;
   441â†’  githubAccessToken: string;
   442â†’}) {
   443â†’  if (!repositoryUrl || !githubUsername) {
   444â†’    logger.info(
   445â†’      'No GitHub repository URL or username available, skipping GitHub cleanup',
   446â†’      {
   447â†’        appId,
   448â†’        hasRepositoryUrl: !!repositoryUrl,
   449â†’        hasGithubUsername: !!githubUsername,
   450â†’      },
   451â†’    );
   452â†’    return;
   453â†’  }
   454â†’
   455â†’  try {
   456â†’    // Parse repository URL to extract owner and repo name
   457â†’    // Expected format: https://github.com/owner/repo
   458â†’    const urlMatch = repositoryUrl.match(/github\.com\/([^\/]+)\/([^\/]+)/);
   459â†’    if (!urlMatch) {
   460â†’      logger.warn('Invalid GitHub repository URL format, skipping cleanup', {
   461â†’        appId,
   462â†’        repositoryUrl,
   463â†’      });
   464â†’      return;
   465â†’    }
   466â†’
   467â†’    const owner = urlMatch[1];
   468â†’    const repo = urlMatch[2];
   469â†’
   470â†’    if (!owner || !repo) {
   471â†’      logger.warn(
   472â†’        'Could not extract owner/repo from GitHub URL, skipping cleanup',
   473â†’        {
   474â†’          appId,
   475â†’          repositoryUrl,
   476â†’        },
   477â†’      );
   478â†’      return;
   479â†’    }
   480â†’
   481â†’    // Only archive repositories from the appdotbuilder organization
   482â†’    if (owner !== DEFAULT_OWNER) {
   483â†’      logger.info(
   484â†’        `Repository is not from ${DEFAULT_OWNER} organization, skipping cleanup`,
   485â†’        {
   486â†’          appId,
   487â†’          owner,
   488â†’          repo,
   489â†’        },
   490â†’      );
   491â†’      return;
   492â†’    }
   493â†’
   494â†’    logger.info('Starting GitHub repository cleanup', {
   495â†’      appId,
   496â†’      owner,
   497â†’      repo,
   498â†’      repositoryUrl,
   499â†’    });
   500â†’
   501â†’    const githubEntity = new GithubEntity(githubUsername, githubAccessToken);
   502â†’    const initializedEntity = await githubEntity.init();
   503â†’    initializedEntity.repo = repo;
   504â†’
   505â†’    const result = await archiveRepository({
   506â†’      githubEntity: initializedEntity,
   507â†’    });
   508â†’
   509â†’    if (result.statusCode === 200) {
   510â†’      logger.info('GitHub repository archived successfully', {
   511â†’        appId,
   512â†’        owner,
   513â†’        repo,
   514â†’        result,
   515â†’      });
   516â†’    } else {
   517â†’      logger.error('Failed to archive GitHub repository', {
   518â†’        appId,
   519â†’        owner,
   520â†’        repo,
   521â†’        error: result.error,
   522â†’      });
   523â†’    }
   524â†’
   525â†’    logger.info('GitHub repository cleanup completed', {
   526â†’      appId,
   527â†’      owner,
   528â†’      repo,
   529â†’    });
   530â†’  } catch (error) {
   531â†’    logger.error('Failed to clean up GitHub repository', {
   532â†’      appId,
   533â†’      repositoryUrl,
   534â†’      error: error instanceof Error ? error.message : error,
   535â†’    });
   536â†’
   537â†’    Instrumentation.captureError(error as Error, {
   538â†’      context: 'cleanup_github_resources',
   539â†’      appId,
   540â†’      repositoryUrl,
   541â†’    });
   542â†’  }
   543â†’}
