:root {
  --radius: 0.75rem;

  /* Warmer backgrounds - subtle warm gray instead of pure gray */
  --background: oklch(0.99 0.005 85);
  --foreground: oklch(0.15 0.01 260);

  /* Warmer cards with slight cream tint */
  --card: oklch(0.98 0.008 85);
  --card-foreground: oklch(0.15 0.01 260);

  /* Warmer popovers */
  --popover: oklch(0.98 0.008 85);
  --popover-foreground: oklch(0.15 0.01 260);

  /* Softer primary - warm charcoal with slight blue undertone */
  --primary: oklch(0.25 0.02 260);
  --primary-foreground: oklch(0.98 0.008 85);

  /* Warmer secondary tones */
  --secondary: oklch(0.95 0.01 85);
  --secondary-foreground: oklch(0.25 0.02 260);

  /* Gentle muted tones with warmth */
  --muted: oklch(0.94 0.015 85);
  --muted-foreground: oklch(0.48 0.02 260);

  /* Soft accent with subtle warmth */
  --accent: oklch(0.93 0.02 85);
  --accent-foreground: oklch(0.25 0.02 260);

  /* Friendlier destructive - less harsh red */
  --destructive: oklch(0.62 0.22 15);

  /* Warmer borders and inputs */
  --border: oklch(0.89 0.015 85);
  --input: oklch(0.87 0.02 85);
  --ring: oklch(0.65 0.03 260);

  /* Keep existing chart colors */
  --chart-1: oklch(0.646 0.222 41.116);
  --chart-2: oklch(0.6 0.118 184.704);
  --chart-3: oklch(0.398 0.07 227.392);
  --chart-4: oklch(0.828 0.189 84.429);
  --chart-5: oklch(0.769 0.188 70.08);

  /* Warmer sidebar */
  --sidebar: oklch(0.98 0.008 85);
  --sidebar-foreground: oklch(0.15 0.01 260);
  --sidebar-primary: oklch(0.25 0.02 260);
  --sidebar-primary-foreground: oklch(0.98 0.008 85);
  --sidebar-accent: oklch(0.93 0.02 85);
  --sidebar-accent-foreground: oklch(0.25 0.02 260);
  --sidebar-border: oklch(0.89 0.015 85);
  --sidebar-ring: oklch(0.65 0.03 260);
}

