     1→import path from 'node:path';
     2→import fs from 'node:fs';
     3→import { exec as execNative } from 'node:child_process';
     4→import { randomBytes } from 'node:crypto';
     5→import { and, eq, isNull } from 'drizzle-orm';
     6→import { createApiClient } from '@neondatabase/api-client';
     7→import { apps, db, deployments } from '../db';
     8→import { logger } from '../logger';
     9→import { promisify } from 'node:util';
    10→import { isProduction } from '../env';
    11→import {
    12→  createRepositoryIfNotExists,
    13→  getImageName,
    14→  generateScopedPullToken,
    15→} from '../ecr';
    16→import { dockerLoginIfNeeded } from '../docker';
    17→import {
    18→  createEcrSecret,
    19→  updateEcrSecret,
    20→  createKoyebApp,
    21→  createKoyebOrganization,
    22→  createKoyebService,
    23→  getOrganizationToken,
    24→  updateKoyebService,
    25→  createKoyebDomain,
    26→  getKoyebDomain,
    27→} from './koyeb';
    28→import type { App } from '../db/schema';
    29→import { DeployStatus, type TemplateId } from '@appdotbuild/core';
    30→import { deployToDatabricks } from './databricks';
    31→
    32→const exec = promisify(execNative);
    33→const NEON_DEFAULT_DATABASE_NAME = 'neondb';
    34→
    35→const neonClient = createApiClient({
    36→  apiKey: process.env.NEON_API_KEY!,
    37→});
    38→
    39→async function deployToKoyeb({
    40→  appId,
    41→  appDirectory,
    42→  currentApp,
    43→  templateId,
    44→}: {
    45→  appId: string;
    46→  appDirectory: string;
    47→  currentApp: Partial<App>;
    48→  templateId: TemplateId;
    49→}) {
    50→  let connectionString: string | undefined;
    51→  let neonProjectId = currentApp.neonProjectId;
    52→
    53→  const githubUsername = currentApp.githubUsername?.toLowerCase();
    54→
    55→  if (neonProjectId) {
    56→    connectionString = await getNeonProjectConnectionString({
    57→      projectId: neonProjectId,
    58→    });
    59→    logger.info('Using existing Neon database', {
    60→      projectId: neonProjectId,
    61→    });
    62→  } else {
    63→    // Create a Neon database
    64→    const { data } = await neonClient.createProject({
    65→      project: {},
    66→    });
    67→    neonProjectId = data.project.id;
    68→    connectionString = data.connection_uris[0]?.connection_uri;
    69→    logger.info('Created Neon database', { projectId: data.project.id });
    70→  }
    71→
    72→  if (!connectionString) {
    73→    throw new Error('Failed to create Neon database');
    74→  }
    75→
    76→  const userDeployments = await db
    77→    .select({
    78→      appId: deployments.appId,
    79→      koyebOrgId: deployments.koyebOrgId,
    80→      koyebOrgName: deployments.koyebOrgName,
    81→      koyebOrgEcrSecretId: deployments.koyebOrgEcrSecretId,
    82→    })
    83→    .from(deployments)
    84→    .where(
    85→      and(
    86→        eq(deployments.ownerId, currentApp.ownerId!),
    87→        isNull(deployments.deletedAt),
    88→      ),
    89→    );
    90→
    91→  // Check if we already have a deployment for this specific app
    92→  const existingAppDeployment = userDeployments.find((d) => d.appId === appId);
    93→  // Get org info from any user deployment
    94→  const orgInfo = userDeployments.find((d) => d.koyebOrgId);
    95→
    96→  let koyebOrgId = orgInfo?.koyebOrgId;
    97→  let koyebOrgName = orgInfo?.koyebOrgName;
    98→  let koyebOrgEcrSecretId = orgInfo?.koyebOrgEcrSecretId;
    99→
   100→  if (!koyebOrgId) {
   101→    ({ koyebOrgId, koyebOrgName } = await createKoyebOrganization(
   102→      githubUsername!,
   103→    ));
   104→  }
   105→
   106→  if (!existingAppDeployment) {
   107→    await db.insert(deployments).values({
   108→      appId,
   109→      ownerId: currentApp.ownerId!,
   110→      koyebOrgId,
   111→      koyebOrgName,
   112→    });
   113→  }
   114→
   115→  await db
   116→    .update(apps)
   117→    .set({
   118→      neonProjectId,
   119→    })
   120→    .where(eq(apps.id, appId));
   121→
   122→  // check if dockerfile exists
   123→  if (!fs.existsSync(path.join(appDirectory, 'Dockerfile'))) {
   124→    throw new Error('Dockerfile not found');
   125→  }
   126→
   127→  const imageName = getImageName(appId, githubUsername!);
   128→
   129→  logger.info('Building Docker image');
   130→
   131→  const buildImagePromise = exec(`docker build -t ${imageName} .`, {
   132→    cwd: appDirectory,
   133→    // @ts-ignore
   134→    stdio: 'inherit',
   135→  });
   136→  const createRepositoryPromise = createRepositoryIfNotExists(
   137→    appId,
   138→    githubUsername!,
   139→  );
   140→
   141→  const koyebAppName = `app-${appId}`;
   142→
   143→  await Promise.all([
   144→    dockerLoginIfNeeded(),
   145→    buildImagePromise,
   146→    createRepositoryPromise,
   147→  ]);
   148→
   149→  logger.info('Pushing Docker image to ECR');
   150→
   151→  await exec(`docker push ${imageName}`, {
   152→    cwd: appDirectory,
   153→  }).then(() => {
   154→    logger.info('Cleaning up Docker image');
   155→    return exec(`docker rmi ${imageName}`);
   156→  });
   157→
   158→  logger.info('Starting Koyeb deployment', { koyebAppName });
   159→
   160→  const userToken = await getOrganizationToken(koyebOrgId);
   161→
   162→  let koyebAppId = currentApp.koyebAppId;
   163→  let koyebServiceId = currentApp.koyebServiceId;
   164→  let koyebDomainId = currentApp.koyebDomainId;
   165→  const scopedPullToken = await generateScopedPullToken(githubUsername!);
   166→
   167→  const ecrParams = {
   168→    token: userToken,
   169→    username: scopedPullToken.username,
   170→    password: scopedPullToken.password,
   171→    url: scopedPullToken.registry.replace('https://', ''),
   172→    koyebOrgId,
   173→  };
   174→
   175→  if (!koyebOrgEcrSecretId) {
   176→    koyebOrgEcrSecretId = await createEcrSecret(ecrParams);
   177→  } else {
   178→    await updateEcrSecret({
   179→      ...ecrParams,
   180→      secretId: koyebOrgEcrSecretId,
   181→    });
   182→  }
   183→
   184→  await db
   185→    .update(deployments)
   186→    .set({
   187→      koyebOrgEcrSecretId,
   188→    })
   189→    .where(
   190→      and(
   191→        eq(deployments.ownerId, currentApp.ownerId!),
   192→        isNull(deployments.deletedAt),
   193→      ),
   194→    );
   195→
   196→  if (!koyebAppId) {
   197→    ({ koyebAppId } = await createKoyebApp({
   198→      koyebAppName,
   199→      token: userToken,
   200→    }));
   201→  }
   202→
   203→  if (!koyebDomainId) {
   204→    ({ koyebDomainId } = await createKoyebDomain({
   205→      koyebAppId,
   206→      koyebAppName,
   207→      token: userToken,
   208→    }));
   209→  }
   210→
   211→  let customEnvs;
   212→  if (templateId === 'nicegui_agent') {
   213→    customEnvs = [
   214→      {
   215→        key: 'NICEGUI_PORT',
   216→        value: '80',
   217→      },
   218→    ];
   219→  } else if (templateId === 'laravel_agent') {
   220→    const unPooledConnectionString = connectionString.replace('-pooler', '');
   221→    // Generate a random 32-byte key and base64 encode it for Laravel
   222→    const appKey = `base64:${randomBytes(32).toString('base64')}`;
   223→    customEnvs = [
   224→      {
   225→        key: 'APP_KEY',
   226→        value: appKey,
   227→      },
   228→      {
   229→        key: 'DB_MIGRATION_URL',
   230→        // To run migrations in Laravel, we need to use the unpooled connection string
   231→        value: unPooledConnectionString,
   232→      },
   233→    ];
   234→  }
   235→
   236→  const params = {
   237→    dockerImage: imageName,
   238→    databaseUrl: connectionString,
   239→    token: userToken,
   240→    customEnvs,
   241→  };
   242→
   243→  let deploymentId: string;
   244→
   245→  if (!koyebServiceId) {
   246→    ({ koyebServiceId, deploymentId } = await createKoyebService({
   247→      ...params,
   248→      koyebAppId,
   249→    }));
   250→  } else {
   251→    ({ deploymentId } = await updateKoyebService({
   252→      ...params,
   253→      serviceId: koyebServiceId,
   254→    }));
   255→  }
   256→
   257→  await db
   258→    .update(apps)
   259→    .set({
   260→      flyAppId: koyebAppName,
   261→      koyebAppId,
   262→      koyebServiceId,
   263→      koyebDomainId,
   264→      deployStatus: DeployStatus.DEPLOYED,
   265→    })
   266→    .where(eq(apps.id, appId));
   267→
   268→  logger.info('Koyeb deployment completed', { koyebAppName });
   269→  logger.info('Updating apps table', {
   270→    koyebAppName,
   271→    appId,
   272→  });
   273→
   274→  const { koyebDomainName } = await getKoyebDomain({
   275→    koyebDomainId,
   276→    token: userToken,
   277→  });
   278→  const appUrl = `https://${koyebDomainName}`;
   279→
   280→  await db
   281→    .update(apps)
   282→    .set({
   283→      appUrl,
   284→    })
   285→    .where(eq(apps.id, appId));
   286→
   287→  if (isProduction) {
   288→    if (fs.existsSync(appDirectory)) {
   289→      fs.rmdirSync(appDirectory, { recursive: true });
   290→    }
   291→  }
   292→
   293→  return { appURL: appUrl, deploymentId };
   294→}
   295→
   296→export async function deployApp({
   297→  appId,
   298→  appDirectory,
   299→  databricksMode,
   300→  templateId,
   301→}: {
   302→  appId: string;
   303→  appDirectory: string;
   304→  databricksMode: boolean;
   305→  templateId: TemplateId;
   306→}) {
   307→  const app = await db
   308→    .select({
   309→      deployStatus: apps.deployStatus,
   310→      neonProjectId: apps.neonProjectId,
   311→      ownerId: apps.ownerId,
   312→      githubUsername: apps.githubUsername,
   313→      koyebAppId: apps.koyebAppId,
   314→      koyebServiceId: apps.koyebServiceId,
   315→      koyebDomainId: apps.koyebDomainId,
   316→      databricksApiKey: apps.databricksApiKey,
   317→      databricksHost: apps.databricksHost,
   318→    })
   319→    .from(apps)
   320→    .where(eq(apps.id, appId));
   321→
   322→  const currentApp = app[0];
   323→
   324→  if (!currentApp) {
   325→    throw new Error(`App ${appId} not found`);
   326→  }
   327→
   328→  // deployed is okay, but deploying is not
   329→  if (currentApp.deployStatus === DeployStatus.DEPLOYING) {
   330→    throw new Error(`App ${appId} is already being deployed`);
   331→  }
   332→
   333→  if (databricksMode) {
   334→    return deployToDatabricks({ appId, appDirectory, currentApp });
   335→  }
   336→
   337→  return deployToKoyeb({ appId, appDirectory, currentApp, templateId });
   338→}
   339→
   340→async function getNeonProjectConnectionString({
   341→  projectId,
   342→}: {
   343→  projectId: string;
   344→}) {
   345→  const branches = await neonClient.listProjectBranches({
   346→    projectId,
   347→  });
   348→  const defaultBranch = branches.data.branches.find((branch) => branch.default);
   349→  const branchId = defaultBranch?.id;
   350→  if (!branchId) {
   351→    throw new Error(`Default branch not found`);
   352→  }
   353→
   354→  const databases = await neonClient.listProjectBranchDatabases(
   355→    projectId,
   356→    branchId,
   357→  );
   358→  const defaultDatabase =
   359→    databases.data.databases.find(
   360→      (db) => db.name === NEON_DEFAULT_DATABASE_NAME,
   361→    ) ?? databases.data.databases[0];
   362→
   363→  if (!defaultDatabase) {
   364→    throw new Error(`Default database not found`);
   365→  }
   366→  const databaseName = defaultDatabase?.name;
   367→  const roleName = defaultDatabase?.owner_name;
   368→
   369→  const connectionString = await neonClient.getConnectionUri({
   370→    projectId,
   371→    database_name: databaseName,
   372→    role_name: roleName,
   373→  });
   374→
   375→  return connectionString.data.uri;
   376→}
