   565→export const getKoyebDeploymentEndpoint = async (
   566→  request: FastifyRequest<{
   567→    Params: { id: string };
   568→    Querystring: { messageId?: string };
   569→  }>,
   570→  reply: FastifyReply,
   571→) => {
   572→  try {
   573→    const { id } = request.params;
   574→    const { messageId } = request.query;
   575→
   576→    if (!id) {
   577→      return reply.status(400).send({ message: 'Deployment ID is required' });
   578→    }
   579→
   580→    const deploymentData = await db
   581→      .select({
   582→        koyebOrgId: deployments.koyebOrgId,
   583→      })
   584→      .from(deployments)
   585→      .where(
   586→        and(
   587→          eq(deployments.ownerId, request.user.id!),
   588→          isNull(deployments.deletedAt),
   589→        ),
   590→      )
   591→      .orderBy(desc(deployments.createdAt))
   592→      .limit(1);
   593→
   594→    const koyebOrgId = deploymentData[0]?.koyebOrgId;
   595→
   596→    if (!koyebOrgId) {
   597→      return reply
   598→        .status(404)
   599→        .send({ message: 'Organization for user not found' });
   600→    }
   601→
   602→    const userToken = await getOrganizationToken(koyebOrgId);
   603→
   604→    const deploymentChecker = getDeploymentChecker({
   605→      deploymentId: id,
   606→      token: userToken,
   607→    });
   608→
   609→    const response = await deploymentChecker();
   610→
   611→    // if received messageId, should update the message with the deployment status
   612→    if (
   613→      messageId &&
   614→      (response.type === 'HEALTHY' || response.type === 'ERROR')
   615→    ) {
   616→      await updateDeploymentMessage(messageId, response.type, response.message);
   617→    }
   618→
   619→    return reply.send(response);
   620→  } catch (error) {
   621→    logger.error('Error getting deployment endpoint', { error });
   622→    return reply.status(500).send({ message: 'Internal server error' });
   623→  }
   624→};
