   634→/**
   635→ * Koyeb validation for org names is this
   636→ * "Use 1 to 39 letters, digits and dash only"
   637→ * So this function is used to normalize the org name to meet the requirements.
   638→ */
   639→function getNormalizedOrgName(orgName: string) {
   640→  // This will replace all non-alphanumeric characters with a dash
   641→  let normalized = orgName.replace(/[^a-zA-Z0-9-]/g, '-');
   642→
   643→  // If there's multiple dashes, they get replaced with one.
   644→  normalized = normalized.replace(/-+/g, '-');
   645→
   646→  if (normalized.length > 39) {
   647→    normalized = normalized.substring(0, 39);
   648→  }
   649→
   650→  return normalized;
   651→}
