   780→async function updateDeploymentMessage(
   781→  messageId: string,
   782→  deploymentType: DeploymentState,
   783→  message: string,
   784→) {
   785→  try {
   786→    const newPlatformType =
   787→      deploymentType === 'HEALTHY'
   788→        ? PlatformMessageType.DEPLOYMENT_COMPLETE
   789→        : PlatformMessageType.DEPLOYMENT_FAILED;
   790→
   791→    const existingMessage = await db
   792→      .select()
   793→      .from(appPrompts)
   794→      .where(eq(appPrompts.id, messageId))
   795→      .limit(1);
   796→
   797→    if (existingMessage.length === 0) {
   798→      return;
   799→    }
   800→
   801→    const currentMetadata = existingMessage[0]?.metadata || {};
   802→    const updatedMetadata = {
   803→      ...currentMetadata,
   804→      type: newPlatformType,
   805→    };
   806→
   807→    await db
   808→      .update(appPrompts)
   809→      .set({
   810→        metadata: updatedMetadata,
   811→        prompt: message,
   812→      })
   813→      .where(eq(appPrompts.id, messageId));
   814→  } catch (error) {
   815→    logger.error('Failed to update deployment message', {
   816→      messageId,
   817→      deploymentType,
   818→      error,
   819→    });
   820→  }
   821→}
