  1011→function scheduleOrganizationDeletion({
  1012→  orgId,
  1013→  token,
  1014→}: {
  1015→  orgId: string;
  1016→  token: string;
  1017→}): Promise<{ deleted: boolean; alreadyDeleted: boolean }> {
  1018→  let attempt = 0;
  1019→  const maxAttempts = 30;
  1020→  const delay = 5_000;
  1021→
  1022→  async function checkStatusAndScheduleNext(): Promise<{
  1023→    deleted: boolean;
  1024→    alreadyDeleted: boolean;
  1025→  }> {
  1026→    attempt++;
  1027→
  1028→    return getKoyebOrganization({ orgId, token })
  1029→      .then(({ organization }) => {
  1030→        logger.info('Organization status check (scheduled)', {
  1031→          orgId,
  1032→          attempt,
  1033→          status: organization.status,
  1034→          maxAttempts,
  1035→        });
  1036→
  1037→        if (organization.status === 'DEACTIVATED') {
  1038→          logger.info('Organization deactivated, proceeding with deletion', {
  1039→            orgId,
  1040→          });
  1041→          return performOrganizationDeletion({ orgId, token });
  1042→        }
  1043→
  1044→        if (attempt >= maxAttempts) {
  1045→          const error = `Failed to confirm organization deactivation after ${maxAttempts} attempts`;
  1046→          logger.error(error, { orgId });
  1047→
  1048→          Instrumentation.captureError(new Error(error), {
  1049→            context: 'schedule_organization_deletion',
  1050→            orgId,
  1051→          });
  1052→
  1053→          throw new Error(error);
  1054→        }
  1055→
  1056→        // Schedule next check with exponential backoff
  1057→        logger.info('Scheduling next status check', {
  1058→          orgId,
  1059→          attempt,
  1060→          nextCheckInMs: delay,
  1061→          maxAttempts,
  1062→        });
  1063→
  1064→        return new Promise<{ deleted: boolean; alreadyDeleted: boolean }>(
  1065→          (resolve, reject) => {
  1066→            setTimeout(() => {
  1067→              checkStatusAndScheduleNext().then(resolve).catch(reject);
  1068→            }, delay);
  1069→          },
  1070→        );
  1071→      })
  1072→      .catch((error) => {
  1073→        logger.error('Error during scheduled organization status check', {
  1074→          orgId,
  1075→          attempt,
  1076→          error: error instanceof Error ? error.message : error,
  1077→        });
  1078→
  1079→        if (attempt >= maxAttempts) {
  1080→          const finalError = new Error(
  1081→            `Failed to verify organization deactivation after ${attempt} attempts: ${error}`,
  1082→          );
  1083→
  1084→          Instrumentation.captureError(finalError, {
  1085→            context: 'schedule_organization_deletion',
  1086→            orgId,
  1087→          });
  1088→
  1089→          throw finalError;
  1090→        }
  1091→
  1092→        // Retry on error with same scheduling logic
  1093→        return new Promise<{ deleted: boolean; alreadyDeleted: boolean }>(
  1094→          (resolve, reject) => {
  1095→            setTimeout(() => {
  1096→              checkStatusAndScheduleNext().then(resolve).catch(reject);
  1097→            }, delay);
  1098→          },
  1099→        );
  1100→      });
  1101→  }
  1102→
  1103→  return checkStatusAndScheduleNext();
  1104→}
  1105→
  1106→async function performOrganizationDeletion({
  1107→  orgId,
  1108→  token,
  1109→}: {
  1110→  orgId: string;
  1111→  token: string;
  1112→}): Promise<{ deleted: boolean; alreadyDeleted: boolean }> {
  1113→  const response = await fetch(
  1114→    `https://app.koyeb.com/v1/organizations/${orgId}`,
  1115→    {
  1116→      method: 'DELETE',
  1117→      headers: {
  1118→        Authorization: `Bearer ${token}`,
  1119→      },
  1120→    },
  1121→  );
  1122→
  1123→  if (!response.ok) {
  1124→    // Handle 404 as already deleted (idempotent)
  1125→    if (response.status === 404) {
  1126→      logger.warn('Koyeb organization already deleted or not found', { orgId });
  1127→      return { deleted: false, alreadyDeleted: true };
  1128→    }
  1129→
  1130→    const error = await response.text();
  1131→    logger.error('Failed to delete Koyeb organization', {
  1132→      status: response.status,
  1133→      statusText: response.statusText,
  1134→      error,
  1135→      orgId,
  1136→    });
  1137→    throw new Error(`Failed to delete Koyeb organization: ${error}`);
  1138→  }
  1139→
  1140→  logger.info('Successfully deleted Koyeb organization', { orgId });
  1141→  return { deleted: true, alreadyDeleted: false };
  1142→}
  1143→
  1144→export async function deleteKoyebOrganization({
  1145→  orgId,
  1146→  token,
  1147→}: {
  1148→  orgId: string;
  1149→  token: string;
  1150→}) {
  1151→  // Initiate deactivation immediately
  1152→  await deactivateKoyebOrganizationAsync({ orgId, token });
  1153→
  1154→  // Return a promise that schedules status polling and eventual deletion
  1155→  return scheduleOrganizationDeletion({ orgId, token });
  1156→}
