import { type DeploymentState, PlatformMessageType } from '@appdotbuild/core';
import { and, desc, eq, isNull } from 'drizzle-orm';
import type { FastifyReply, FastifyRequest } from 'fastify';
import { db } from '../db';
import { appPrompts, deployments } from '../db/schema';
import { Instrumentation } from '../instrumentation';
import { logger } from '../logger';

type KoyebOrgSwitchResponse = {
  token: {
    id: string;
    user_id: string;
    organization_id: string;
    expires_at: string;
  };
};

type CreateKoyebOrganizationResponse = {
  organization: {
    id: string;
  };
};

type CreateKoyebAppResponse = {
  app: {
    id: string;
  };
};
type KoyebEnv = {
  scopes?: Array<string>;
  key: string;
  value: string;
};
type KoyebOrganizationPlan =
  | 'hobby'
  | 'starter'
  | 'startup'
  | 'business'
  | 'enterprise'
  | 'internal'
  | 'hobby23'
  | 'no_plan'
  | 'pro'
  | 'scale'
  | 'partner_csp'
  | 'partner_csp_unit';

type KoyebOrganizationStatus =
  | 'WARNING'
  | 'LOCKED'
  | 'ACTIVE'
  | 'DEACTIVATING'
  | 'DEACTIVATED'
  | 'DELETING'
  | 'DELETED';

type KoyebOrganizationDetailedStatus =
  | 'NEW'
  | 'EMAIL_NOT_VALIDATED'
  | 'BILLING_INFO_MISSING'
  | 'LOCKED'
  | 'PAYMENT_FAILURE'
  | 'VALID'
  | 'PENDING_VERIFICATION'
  | 'VERIFICATION_FAILED'
  | 'REVIEWING_ACCOUNT'
  | 'PLAN_UPGRADE_REQUIRED';

type KoyebOrganizationDeactivationReason =
  | 'INVALID'
  | 'REQUESTED_BY_OWNER'
  | 'SUBSCRIPTION_TERMINATION'
