import type {
  DeployStatusType,
  MessageKind,
  PromptKindType,
  TraceId,
} from '../agent-message';

export type UserMessageLimit = {
  isUserLimitReached: boolean;
  dailyMessageLimit: number;
  remainingMessages: number;
  currentUsage: number;
  nextResetTime: Date;
};

export type PlatformAppsLimitHeaders = {
  'x-daily-apps-limit': number;
  'x-daily-apps-reset': string; // ISO string of Date
};

export type MessageLimitHeaders = {
  'x-dailylimit-limit': number;
  'x-dailylimit-remaining': number;
  'x-dailylimit-usage': number;
  'x-dailylimit-reset': string; // ISO string of Date
};

type Pagination = {
  total: number;
  page: number;
  limit: number;
  totalPages: number;
};

export type Paginated<T> = {
  data: T[];
  pagination: Pagination;
};

export type AppPrompts = {
  id: string;
  appId: string;
  prompt: string;
  response: string;
  kind: PromptKindType;
  messageKind?: MessageKind;
  metadata?: any;
  createdAt: Date;
  updatedAt: Date;
};

export type App = {
  id: string;
  name: string;
  traceId: string;
  createdAt: Date;
  updatedAt: Date;
  ownerId: string;
  flyAppId?: string | null;
  s3Checksum?: string | null;
  deployStatus?: DeployStatusType;
  typespecSchema?: string | null;
  receivedSuccess: boolean;
  recompileInProgress: boolean;
  clientSource: string;
  repositoryUrl?: string | null;
  appName?: string | null;
  appUrl?: string | null;
  techStack: TemplateId;
};

export type User = {
  id: string;
  email: string;
  name: string | null;
  createdAt: Date;
  updatedAt: Date;
  rawJson: string;
};

export type TemplateId = 'trpc_agent' | 'nicegui_agent' | 'laravel_agent';

export const DEPLOYMENT_STATE = {
  HEALTHY: 'HEALTHY',
  ERROR: 'ERROR',
  STOPPING: 'STOPPING',
  DEPLOYMENT_IN_PROGRESS: 'DEPLOYMENT_IN_PROGRESS',
} as const;
export type DeploymentState =
  (typeof DEPLOYMENT_STATE)[keyof typeof DEPLOYMENT_STATE];

export type MessageSSERequest = {
  message: string;
  clientSource: string;
  environment?: 'staging' | 'production';
  settings?: Record<string, any>;
  applicationId?: string;
  traceId?: TraceId;
  databricksApiKey?: string;
  databricksHost?: string;
  templateId?: TemplateId;
};

