export function useDeploymentStatus(
  deploymentId: string | undefined,
  messageId?: string,
  options?: {
    enabled?: boolean;
    onSuccess?: (data: DeploymentStatusResponse) => void;
    onError?: (error: Error) => void;
  },
) {
