};

type Pagination = {
  total: number;
  page: number;
  limit: number;
  totalPages: number;
};

export type Paginated<T> = {
  data: T[];
  pagination: Pagination;
};

export type AppPrompts = {
  id: string;
  appId: string;
  prompt: string;
  response: string;
  kind: PromptKindType;
