  UserMessageLimit,
} from '@appdotbuild/core';

import { apiClient } from '../api/adapter';

export type PaginatedResponse<T> = {
  data: T[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
  };
};

export type DeploymentStatusResponse = {
  message: string;
  isDeployed: boolean;
  type: DeploymentState;
};

