   347→  });
   348→  const defaultBranch = branches.data.branches.find((branch) => branch.default);
   349→  const branchId = defaultBranch?.id;
   350→  if (!branchId) {
   351→    throw new Error(`Default branch not found`);
   352→  }
   353→
   354→  const databases = await neonClient.listProjectBranchDatabases(
