  1025→function storeDevLogs(
  1026→  parsedMessage: AgentSseEvent,
  1027→  messageWithoutData: string,
  1028→) {
  1029→  if (isDev) {
  1030→    const separator = '--------------------------------';
  1031→
  1032→    if (parsedMessage.message.unifiedDiff) {
  1033→      fs.writeFileSync(
  1034→        `${logsFolder}/unified_diff-${Date.now()}.patch`,
  1035→        `${parsedMessage.message.unifiedDiff}\n\n`,
  1036→      );
  1037→    }
  1038→    fs.writeFileSync(
  1039→      `${logsFolder}/sse_messages.log`,
  1040→      `${separator}\n\n${messageWithoutData}\n\n`,
  1041→    );
  1042→  }
  1043→}
