  1146→async function appIteration({
  1147→  appName,
  1148→  githubEntity,
  1149→  files,
  1150→  agentState,
  1151→  applicationId,
  1152→  traceId,
  1153→  session,
  1154→  commitMessage,
  1155→  userId,
  1156→}: {
  1157→  appName: string;
  1158→  githubEntity: GithubEntityInitialized;
  1159→  files: ReturnType<typeof readDirectoryRecursive>;
  1160→  applicationId: string;
  1161→  traceId: string;
  1162→  session: Session;
  1163→  agentState: AgentSseEvent['message']['agentState'];
  1164→  commitMessage: string;
  1165→  userId: string;
  1166→}) {
  1167→  const commitStartTime = Instrumentation.trackGitHubCommit();
  1168→
  1169→  const { commitSha } = await commitChanges({
  1170→    githubEntity,
  1171→    paths: files,
  1172→    message: commitMessage,
  1173→    branch: 'main',
  1174→  });
  1175→
  1176→  Instrumentation.trackGitHubCommitEnd(commitStartTime);
  1177→
  1178→  githubEntity.repositoryUrl = `https://github.com/${githubEntity.owner}/${appName}`;
  1179→
  1180→  if (agentState) {
  1181→    await db
  1182→      .update(apps)
  1183→      .set({
  1184→        agentState: agentState,
  1185→      })
  1186→      .where(eq(apps.id, applicationId));
  1187→  }
  1188→
  1189→  const commitUrl = `https://github.com/${githubEntity.owner}/${appName}/commit/${commitSha}`;
  1190→
  1191→  await pushAndSavePlatformMessage(
  1192→    session,
  1193→    applicationId,
  1194→    new PlatformMessage(
  1195→      AgentStatus.IDLE,
  1196→      traceId as TraceId,
  1197→      applicationId,
  1198→      `Changes have been committed to your repository:`,
  1199→      {
  1200→        type: PlatformMessageType.COMMIT_CREATED,
  1201→        commitUrl: commitUrl,
  1202→      },
  1203→    ),
  1204→    userId,
  1205→  );
  1206→}
