  1208→function getExistingConversationBody({
  1209→  existingConversation,
  1210→  userMessage,
  1211→  settings,
  1212→  existingTraceId,
  1213→  applicationId,
  1214→  templateId,
  1215→}: {
  1216→  existingConversation: ConversationData;
  1217→  existingTraceId: string;
  1218→  applicationId: string;
  1219→  userMessage: string;
  1220→  settings?: Record<string, any>;
  1221→  templateId: TemplateId;
  1222→}) {
  1223→  const messages = existingConversation.allMessages;
  1224→
  1225→  return {
  1226→    allMessages: [
  1227→      ...messages,
  1228→      {
  1229→        role: PromptKind.USER,
  1230→        content: userMessage,
  1231→      },
  1232→    ],
  1233→    agentState: existingConversation.agentState,
  1234→    traceId: existingTraceId,
  1235→    applicationId,
  1236→    settings: settings || {},
  1237→    templateId,
  1238→  };
  1239→}
  1240→
  1241→function createStreamLogger(
  1242→  session: Session,
  1243→  isPrivilegedUser: boolean,
  1244→): StreamLogFunction {
  1245→  return function streamLog(
  1246→    logData: StructuredLog,
  1247→    level: 'info' | 'error' = 'info',
  1248→  ) {
  1249→    app.log[level](logData);
  1250→
  1251→    // only push if is privileged user
  1252→    if (isPrivilegedUser) {
  1253→      session.push(
  1254→        { log: logData.message, level, appId: logData.applicationId },
  1255→        'debug',
  1256→      );
  1257→    }
  1258→  };
  1259→}
  1260→
  1261→async function getMessagesFromHistory(
  1262→  applicationId: string,
  1263→  userId: string,
  1264→): Promise<ConversationMessage[]> {
  1265→  if (conversationManager.hasConversation(applicationId)) {
  1266→    // for temp apps, first check in-memory
  1267→    const memoryMessages =
  1268→      conversationManager.getConversationHistory(applicationId);
  1269→    if (memoryMessages.length > 0) {
  1270→      return memoryMessages;
  1271→    }
  1272→    // fallback for corner cases
  1273→    return await getMessagesFromDB(applicationId, userId);
  1274→  }
  1275→
  1276→  // for permanent apps, fetch from db
  1277→  return await getMessagesFromDB(applicationId, userId);
  1278→}
  1279→
  1280→async function getMessagesFromDB(
  1281→  applicationId: string,
  1282→  userId: string,
  1283→): Promise<ConversationMessage[]> {
  1284→  const history = await getAppPromptHistoryForAgent(applicationId, userId);
  1285→
  1286→  if (!history || history.length === 0) {
  1287→    return [];
  1288→  }
  1289→
  1290→  return history
  1291→    .filter((prompt) => {
  1292→      // platform messages should not go to agent
  1293→      return prompt.messageKind !== MessageKind.PLATFORM_MESSAGE;
  1294→    })
  1295→    .map((prompt) => {
  1296→      if (prompt.kind === PromptKind.USER) {
  1297→        return {
  1298→          role: PromptKind.USER,
  1299→          content: prompt.prompt,
  1300→          kind: MessageKind.USER_MESSAGE,
  1301→        };
  1302→      }
  1303→      return {
  1304→        role: PromptKind.ASSISTANT,
  1305→        content: prompt.prompt,
  1306→        kind: (prompt.messageKind || MessageKind.STAGE_RESULT) as MessageKind,
  1307→        metadata: prompt.metadata,
  1308→      };
  1309→    });
  1310→}
