     1→import { exec as execNative } from 'node:child_process';
     2→import { eq } from 'drizzle-orm';
     3→import { apps, db } from '../db';
     4→import { logger } from '../logger';
     5→import { promisify } from 'node:util';
     6→import type { App } from '../db/schema';
     7→import { getOrCreateNeonProject } from './neon';
     8→import fs from 'node:fs';
     9→import { DeployStatus } from '@appdotbuild/core';
    10→import z from 'zod';
    11→
    12→const exec = promisify(execNative);
    13→
    14→const DATABASE_URL_ENV_KEY = 'APP_DATABASE_URL';
    15→const DATABASE_URL_RESOURCE_KEY = 'APP_DATABASE_URL';
    16→
    17→const databricksScopeSchema = z.object({
    18→  key: z.string(),
    19→  value: z.string(),
    20→});
    21→
    22→export async function deployToDatabricks({
    23→  appId,
    24→  appDirectory,
    25→  currentApp,
    26→}: {
    27→  appId: string;
    28→  appDirectory: string;
    29→  currentApp: Partial<App>;
    30→}) {
    31→  // Validate appId to prevent shell injection
    32→  validateAppId(appId);
    33→
    34→  if (!currentApp.databricksApiKey || !currentApp.databricksHost) {
    35→    throw new Error(
    36→      'Databricks API key and host are required for Databricks deployment',
    37→    );
    38→  }
    39→
    40→  const { connectionString, neonProjectId } = await getOrCreateNeonProject({
    41→    existingNeonProjectId: currentApp.neonProjectId ?? undefined,
    42→  });
    43→
    44→  // Update app status to deploying for databricks deployments
    45→  await db
    46→    .update(apps)
    47→    .set({
    48→      deployStatus: DeployStatus.DEPLOYING,
    49→      neonProjectId,
    50→    })
    51→    .where(eq(apps.id, appId));
    52→
    53→  // Create isolated environment variables for this deployment
    54→  const databricksEnv = {
    55→    ...process.env,
    56→    DATABRICKS_HOST: currentApp.databricksHost,
