    79→  clientSource: string;
    80→  environment?: 'staging' | 'production';
    81→  settings?: Record<string, any>;
    82→  applicationId?: string;
    83→  traceId?: TraceId;
    84→  databricksApiKey?: string;
    85→  databricksHost?: string;
