    87→};
    88→
    89→type StructuredLog = {
    90→  message: string;
    91→  applicationId?: string;
    92→  traceId?: string;
    93→  [key: string]: any;
    94→};
    95→
    96→type DBMessage = {
    97→  appId: string;
    98→  message: string;
    99→  role: PromptKindType;
   100→  messageKind: MessageKind;
   101→  metadata?: any;
   102→};
   103→
   104→export type StreamLogFunction = (
   105→  logData: StructuredLog,
   106→  level?: 'info' | 'error',
   107→) => void;
   108→
   109→const logsFolder = path.join(__dirname, '..', '..', 'logs');
   110→
   111→const appExistsInDb = async (
   112→  applicationId: string | undefined,
   113→): Promise<boolean> => {
   114→  if (!applicationId || !validate(applicationId)) {
   115→    return false;
   116→  }
   117→
   118→  const exists = await db
   119→    .select({ exists: sql`1` })
   120→    .from(apps)
   121→    .where(eq(apps.id, applicationId))
   122→    .limit(1);
   123→
   124→  const appExists = exists.length > 0;
   125→
   126→  return appExists;
   127→};
   128→
   129→const generateTraceId = (
   130→  request: FastifyRequest,
   131→  applicationId: string,
   132→): TraceId => `app-${applicationId}.req-${request.id}`;
   133→
   134→export async function postMessage(
   135→  request: FastifyRequest,
   136→  reply: FastifyReply,
   137→) {
   138→  const userId = request.user.id;
   139→  const isPrivilegedUser = request.user.isPrivilegedUser;
   140→
   141→  const {
   142→    isUserLimitReached,
   143→    dailyMessageLimit,
   144→    nextResetTime,
   145→    remainingMessages,
   146→    currentUsage,
   147→  } = await checkMessageUsageLimit(userId);
   148→  if (isUserLimitReached) {
   149→    app.log.error({
   150→      message: 'Daily message limit reached',
   151→      userId,
   152→    });
   153→    return reply.status(429).send({
   154→      code: API_ERROR_CODE.DAILY_MESSAGE_LIMIT_REACHED,
   155→      message: 'Daily message limit reached. Please try again tomorrow.',
   156→    });
   157→  }
   158→
   159→  const requestBody = request.body as RequestBody;
   160→  let applicationId = requestBody.applicationId;
   161→  let isPermanentApp = await appExistsInDb(applicationId);
   162→  const hasReachedPlatformLimit = await userReachedPlatformLimit(request);
   163→  if (!isPermanentApp && hasReachedPlatformLimit) {
   164→    app.log.error({
   165→      message: 'Daily apps limit reached.',
   166→      userId,
   167→    });
   168→    Instrumentation.captureError(new Error('Daily apps limit reached'), {
   169→      userId,
   170→      context: API_ERROR_CODE.DAILY_APPS_LIMIT_REACHED,
   171→    });
   172→
   173→    const platformAppsLimitHeaders: PlatformAppsLimitHeaders = {
   174→      'x-daily-apps-limit': DAILY_APPS_LIMIT,
   175→      'x-daily-apps-reset': getAppsDailyLimitResetTime().toISOString(),
   176→    };
   177→    reply.headers(platformAppsLimitHeaders);
   178→    return reply.status(429).send({
   179→      code: API_ERROR_CODE.DAILY_APPS_LIMIT_REACHED,
   180→      message: 'Platform daily apps limit reached. Please try again tomorrow.',
   181→    });
   182→  }
   183→
   184→  const userLimitHeader: MessageLimitHeaders = {
   185→    'x-dailylimit-limit': dailyMessageLimit,
   186→    'x-dailylimit-remaining': remainingMessages,
   187→    'x-dailylimit-usage': currentUsage,
   188→    'x-dailylimit-reset': nextResetTime.toISOString(),
   189→  };
   190→
   191→  const sseCORS = {
   192→    'Access-Control-Allow-Origin': request.headers.origin,
   193→    'Access-Control-Allow-Methods': 'POST',
   194→    'Access-Control-Allow-Headers':
   195→      'Content-Type, Authorization, Accept, Accept-Encoding, Connection, Cache-Control,',
   196→    'Access-Control-Expose-Headers': Object.keys(userLimitHeader).join(', '),
   197→    'Access-Control-Allow-Credentials': 'true',
   198→  };
   199→
   200→  reply.headers(userLimitHeader);
   201→
   202→  const session = await createSession(request.raw, reply.raw, {
   203→    headers: {
   204→      ...userLimitHeader,
   205→      ...sseCORS,
   206→    },
   207→  });
   208→
   209→  const streamLog = createStreamLogger(session, isPrivilegedUser);
   210→  const abortController = new AbortController();
   211→  const githubUsername = request.user.githubUsername;
   212→  const githubAccessToken = request.user.githubAccessToken;
   213→  let traceId = requestBody.traceId;
   214→
   215→  request.socket.on('close', () => {
   216→    streamLog({
   217→      message: 'Client disconnected',
   218→      applicationId,
   219→      traceId,
   220→      userId,
   221→    });
   222→    abortController.abort();
   223→  });
   224→
   225→  streamLog({
   226→    message: 'Created SSE session',
   227→    applicationId,
   228→    traceId,
   229→    userId,
   230→  });
   231→
   232→  Instrumentation.addTags({
   233→    'request.type': 'sse',
   234→    'request.has_application_id': !!applicationId,
   235→    'request.environment': requestBody.environment || 'production',
   236→  });
   237→
   238→  Instrumentation.trackSseEvent('sse_connection_started', {
   239→    applicationId,
   240→    userId,
   241→  });
   242→
   243→  if (isDev) {
   244→    fs.mkdirSync(logsFolder, { recursive: true });
   245→  }
   246→
   247→  streamLog({
   248→    message: 'Received message request',
   249→    applicationId,
   250→    traceId,
   251→    userId,
   252→    requestBody: request.body,
   253→  });
   254→
   255→  Instrumentation.trackUserMessage(requestBody.message, userId);
   256→
   257→  try {
   258→    const githubEntity = await new GithubEntity(
   259→      githubUsername,
   260→      githubAccessToken,
   261→    ).init();
   262→
   263→    let templateId = requestBody.templateId || 'trpc_agent';
   264→    // databricks apps only support python apps for now
   265→    if (requestBody.databricksHost) {
   266→      templateId = 'nicegui_agent';
   267→    }
   268→
   269→    let body: Optional<Body, 'traceId'> = {
   270→      applicationId,
   271→      allMessages: [
   272→        {
   273→          role: PromptKind.USER,
   274→          content: requestBody.message,
   275→        },
   276→      ],
   277→      settings: requestBody.settings || {},
   278→      templateId,
   279→    };
   280→
   281→    let appName: string | null = null;
   282→    if (applicationId) {
   283→      app.log.info({
   284→        message: 'Processing existing applicationId',
   285→        applicationId,
   286→        userId,
   287→      });
   288→
   289→      if (isPermanentApp) {
   290→        const application = await db
   291→          .select()
   292→          .from(apps)
   293→          .where(
   294→            and(
   295→              eq(apps.id, applicationId),
   296→              eq(apps.ownerId, userId),
   297→              isNull(apps.deletedAt),
   298→            ),
   299→          );
   300→
   301→        if (application.length === 0) {
   302→          streamLog(
   303→            {
   304→              message: 'Application not found',
   305→              applicationId,
   306→              userId,
   307→            },
   308→            'error',
   309→          );
   310→          return reply.status(404).send({
   311→            error: 'Application not found',
   312→            status: 'error',
   313→          });
   314→        }
   315→
   316→        streamLog(
   317→          {
   318→            message: 'Application found',
   319→            applicationId: application[0]?.id,
   320→            userId,
   321→          },
   322→          'info',
   323→        );
   324→        appName = application[0]!.appName;
   325→        templateId = application[0]!.techStack as TemplateId;
   326→
   327→        if (application[0]!.repositoryUrl) {
   328→          githubEntity.repositoryUrl = application[0]!.repositoryUrl;
   329→        }
   330→
   331→        const messagesFromHistory = await getMessagesFromHistory(
   332→          applicationId,
   333→          userId,
   334→        );
   335→
   336→        //add existing messages to in-memory conversation
   337→        conversationManager.addMessagesToConversation(
   338→          applicationId,
   339→          messagesFromHistory,
   340→          templateId,
   341→        );
   342→
   343→        body = {
   344→          ...body,
   345→          applicationId,
   346→          traceId,
   347→          agentState: application[0]!.agentState,
   348→          allMessages: [
   349→            ...messagesFromHistory,
   350→            {
   351→              role: PromptKind.USER,
   352→              content: requestBody.message,
   353→            },
   354→          ],
   355→        };
   356→
   357→        streamLog({
   358→          message: 'Loaded messages from history',
   359→          applicationId,
   360→          traceId,
   361→          userId,
   362→          messageCount: messagesFromHistory.length,
   363→        });
   364→      } else {
   365→        // for temporary apps, we need to get the previous request from the memory
   366→        const existingConversation =
   367→          conversationManager.getConversation(applicationId);
   368→        templateId = existingConversation?.techStack || templateId;
   369→        if (!existingConversation) {
   370→          streamLog(
   371→            {
   372→              message: 'Previous request not found',
   373→              applicationId,
   374→              userId,
   375→            },
   376→            'error',
   377→          );
   378→          terminateStreamWithError(
   379→            session,
   380→            'Previous request not found',
   381→            abortController,
   382→            traceId as TraceId,
   383→          );
   384→          return;
   385→        }
   386→
   387→        body = {
   388→          ...body,
   389→          ...getExistingConversationBody({
   390→            existingConversation,
   391→            existingTraceId: traceId as TraceId,
   392→            applicationId,
   393→            userMessage: requestBody.message,
   394→            settings: requestBody.settings,
   395→            templateId,
   396→          }),
   397→        };
   398→      }
   399→    } else {
   400→      applicationId = uuidv4();
   401→      traceId = generateTraceId(request, applicationId);
   402→      body = {
   403→        ...body,
   404→        applicationId,
   405→        traceId,
   406→      };
   407→    }
   408→
   409→    // add user message to conversation
   410→    conversationManager.addUserMessageToConversation(
   411→      applicationId,
   412→      requestBody.message,
   413→      templateId,
   414→    );
   415→
   416→    // Save user message to database immediately for permanent apps
   417→    if (isPermanentApp) {
   418→      await saveMessageToDB({
   419→        appId: applicationId,
   420→        message: requestBody.message,
   421→        role: PromptKind.USER,
   422→        messageKind: MessageKind.USER_MESSAGE,
   423→      });
   424→    }
   425→
   426→    const tempDirPath = path.join(
   427→      os.tmpdir(),
   428→      `appdotbuild-template-${Date.now()}`,
   429→    );
   430→
   431→    const volumePromise = isPermanentApp
   432→      ? cloneRepository({
   433→          repo: `${githubEntity.owner}/${appName}`,
   434→          githubAccessToken: githubEntity.githubAccessToken,
   435→          tempDirPath,
   436→        })
   437→          .then(copyDirToMemfs)
   438→          .catch((error) => {
   439→            streamLog(
   440→              {
   441→                message: 'Error cloning repository',
   442→                error: error instanceof Error ? error.message : String(error),
   443→                applicationId,
   444→                traceId,
   445→                userId,
   446→              },
   447→              'error',
   448→            );
   449→            terminateStreamWithError(
   450→              session,
   451→              'There was an error cloning your repository, try again with a different prompt.',
   452→              abortController,
   453→              traceId as TraceId,
   454→            );
   455→            return reply.status(500);
   456→          })
   457→      : createMemoryFileSystem();
   458→
   459→    // We are iterating over an existing app, so we wait for the promise here to read the files that where cloned.
   460→    // and we add them to the body for the agent to use.
   461→    if (isPermanentApp && volumePromise) {
   462→      const { volume, virtualDir } = await volumePromise;
   463→      body.allFiles = readDirectoryRecursive(virtualDir, virtualDir, volume);
   464→    }
   465→
   466→    if (isDev) {
   467→      fs.writeFileSync(
   468→        `${logsFolder}/${applicationId}-body.json`,
   469→        JSON.stringify(JSON.stringify(body), null, 2),
   470→      );
   471→    }
   472→
   473→    streamLog(
   474→      {
   475→        message: 'Sending request to agent',
   476→        applicationId,
   477→        traceId,
   478→        userId,
   479→        agentHost: getAgentHost(requestBody.environment),
   480→        body: JSON.stringify(body),
   481→      },
   482→      'info',
   483→    );
   484→    Instrumentation.trackAiAgentStart(traceId!, applicationId);
   485→
   486→    let canDeploy = false;
   487→    const requestStartTime = Date.now();
   488→    const agentUrl = `${getAgentHost(requestBody.environment)}/message`;
   489→    const messageHandlerQueue = new MessageHandlerQueue(streamLog);
   490→    await nodeEventSource(agentUrl, {
   491→      method: 'POST',
   492→      headers: {
   493→        Accept: 'text/event-stream',
   494→        'Accept-Encoding': 'br, gzip, deflate',
   495→        Authorization: `Bearer ${process.env.AGENT_API_SECRET_AUTH}`,
   496→        Connection: 'keep-alive',
   497→        'Content-Type': 'application/json',
   498→        'Cache-Control': 'no-cache',
   499→      },
   500→      data: body,
   501→      signal: abortController.signal,
   502→      onOpen(response) {
   503→        const connectionTime = Date.now() - requestStartTime;
   504→        streamLog(
   505→          {
   506→            message: `[${new Date().toISOString()}] [appId: ${applicationId}] Connection established after ${connectionTime}ms - Status: ${
   507→              response.status
   508→            }, StatusText: ${response.statusText}`,
   509→            applicationId,
   510→            traceId,
   511→            userId,
   512→          },
   513→          'info',
   514→        );
   515→
   516→        // Check if the response is actually successful
   517→        if (response.status !== 200) {
   518→          throw new Error(`HTTP ${response.status}: ${response.statusText}`);
   519→        }
   520→      },
   521→      async onMessage(ev) {
   522→        const messageHandler = async () => {
   523→          try {
   524→            // Early exit if session is disconnected
   525→            if (!session.isConnected) {
   526→              app.log.debug(
   527→                `[appId: ${applicationId}] Session disconnected, skipping message processing`,
   528→              );
   529→              return;
   530→            }
   531→
   532→            if (!applicationId) {
   533→              streamLog(
   534→                {
   535→                  message: `[appId: ${applicationId}] Application ID is not set, skipping message`,
   536→                  applicationId,
   537→                  traceId,
   538→                  userId,
   539→                },
   540→                'error',
   541→              );
   542→              return;
   543→            }
   544→
   545→            const message = ev.data;
   546→            if (isDev) {
   547→              fs.appendFileSync(
   548→                `${logsFolder}/sse_messages-${applicationId}.log`,
   549→                `data: ${message}\n\n`,
   550→              );
   551→            }
   552→
   553→            const parsedEvent = JSON.parse(message);
   554→            let completeParsedMessage: AgentSseEvent;
   555→            try {
   556→              completeParsedMessage = agentSseEventSchema.parse(parsedEvent);
   557→            } catch (error) {
   558→              streamLog(
   559→                {
   560→                  message: `[appId: ${applicationId}] Error validating schema for message: ${JSON.stringify(
   561→                    parsedEvent,
   562→                  )}. Error: ${JSON.stringify(error)}`,
   563→                  applicationId,
   564→                  traceId,
   565→                  userId,
   566→                },
   567→                'error',
   568→              );
   569→              terminateStreamWithError(
   570→                session,
   571→                'There was an error validating the message schema, try again with a different prompt.',
   572→                abortController,
   573→                traceId as TraceId,
   574→              );
   575→              return;
   576→            }
   577→
   578→            if (parsedEvent.message.kind === 'KeepAlive') {
   579→              streamLog(
   580→                {
   581→                  message: `[appId: ${applicationId}] keep alive message received`,
   582→                  applicationId,
   583→                  traceId,
   584→                  userId,
   585→                },
   586→                'info',
   587→              );
   588→              return;
   589→            }
   590→
   591→            storeDevLogs(completeParsedMessage, message);
   592→            conversationManager.addConversation(
   593→              applicationId,
   594→              completeParsedMessage,
   595→              templateId,
   596→            );
   597→
   598→            // save agent messages to database immediately
   599→            if (isPermanentApp) {
   600→              await saveAgentMessages(applicationId, completeParsedMessage);
   601→            }
   602→
   603→            // unifiedDiff and agentState are not needed by the CLI
   604→            const { unifiedDiff, agentState, ...minimalMessage } =
   605→              completeParsedMessage.message;
   606→
   607→            const parsedCLIMessage = {
   608→              ...completeParsedMessage,
   609→              appId: applicationId,
   610→              techStack: templateId,
   611→              message: minimalMessage,
   612→            };
   613→
   614→            streamLog({
   615→              message: `message sent to CLI with status: ${parsedCLIMessage.status}, kind: ${parsedCLIMessage.message.kind}`,
   616→              applicationId,
   617→              traceId,
   618→              userId,
   619→            });
   620→
   621→            Instrumentation.trackSseEvent('sse_message_sent', {
   622→              messageKind: minimalMessage.kind,
   623→              status: completeParsedMessage.status,
   624→              userId,
   625→            });
   626→
   627→            session.push(parsedCLIMessage);
   628→            if (
   629→              completeParsedMessage.message.unifiedDiff ===
   630→              '# Note: This is a valid empty diff (means no changes from template)'
   631→            ) {
   632→              completeParsedMessage.message.unifiedDiff = null;
   633→            }
   634→
   635→            if (
   636→              completeParsedMessage.message.unifiedDiff?.startsWith(
   637→                '# ERROR GENERATING DIFF',
   638→              )
   639→            ) {
   640→              terminateStreamWithError(
   641→                session,
   642→                'There was an error generating your application diff, try again with a different prompt.',
   643→                abortController,
   644→                traceId as TraceId,
   645→              );
   646→              return;
   647→            }
   648→
   649→            canDeploy = !!completeParsedMessage.message.unifiedDiff;
   650→
   651→            if (canDeploy) {
   652→              streamLog(
   653→                {
   654→                  message: `[appId: ${applicationId}] starting to deploy app`,
   655→                  applicationId,
   656→                  traceId,
   657→                  userId,
   658→                },
   659→                'info',
   660→              );
   661→              const { volume, virtualDir, memfsVolume } = await volumePromise;
   662→              const unifiedDiffPath = path.join(
   663→                virtualDir,
   664→                `unified_diff-${Date.now()}.patch`,
   665→              );
   666→
   667→              streamLog(
   668→                {
   669→                  message: `[appId: ${applicationId}] writing unified diff to file, virtualDir: ${unifiedDiffPath}`,
   670→                  applicationId,
   671→                  traceId,
   672→                  userId,
   673→                },
   674→                'info',
   675→              );
   676→              volume.writeFileSync(
   677→                unifiedDiffPath,
   678→                `${completeParsedMessage.message.unifiedDiff}\n\n`,
   679→              );
   680→              const repositoryPath = await applyDiff(
   681→                unifiedDiffPath,
   682→                virtualDir,
   683→                volume,
   684→              );
   685→              const files = readDirectoryRecursive(
   686→                repositoryPath,
   687→                virtualDir,
   688→                volume,
   689→              );
   690→
   691→              if (isDev) {
   692→                fs.writeFileSync(
   693→                  `${logsFolder}/${applicationId}-files.json`,
   694→                  JSON.stringify(files, null, 2),
   695→                );
   696→              }
   697→
   698→              if (isPermanentApp && appName) {
   699→                streamLog(
   700→                  {
   701→                    message: `[appId: ${applicationId}] app iteration`,
   702→                    applicationId,
   703→                    traceId,
   704→                    userId,
   705→                  },
   706→                  'info',
   707→                );
   708→                githubEntity.repo = appName;
   709→                await appIteration({
   710→                  appName: appName,
   711→                  githubEntity,
   712→                  files,
   713→                  agentState: completeParsedMessage.message.agentState,
   714→                  applicationId,
   715→                  traceId: traceId!,
   716→                  session,
   717→                  commitMessage:
   718→                    completeParsedMessage.message.commit_message ||
   719→                    'feat: update',
   720→                  userId,
   721→                });
   722→              } else if (
   723→                completeParsedMessage.message.kind !==
   724→                MessageKind.REFINEMENT_REQUEST
   725→              ) {
   726→                streamLog(
   727→                  {
   728→                    message: `[appId: ${applicationId}] creating new app`,
   729→                    applicationId,
   730→                    traceId,
   731→                    userId,
   732→                  },
   733→                  'info',
   734→                );
   735→
   736→                const appCreationStartTime =
   737→                  Instrumentation.trackAppCreationStart();
   738→
   739→                appName =
   740→                  completeParsedMessage.message.app_name ||
   741→                  `app.build-${uuidv4().slice(0, 4)}`;
   742→
   743→                const { newAppName } = await appCreation({
   744→                  applicationId: applicationId!,
   745→                  techStack: templateId,
   746→                  appName,
   747→                  traceId: traceId!,
   748→                  agentState: completeParsedMessage.message.agentState,
   749→                  githubEntity,
   750→                  ownerId: request.user.id,
   751→                  session,
   752→                  requestBody,
   753→                  files,
   754→                  streamLog,
   755→                });
   756→
   757→                appName = newAppName;
   758→                isPermanentApp = true;
   759→
   760→                Instrumentation.trackAppCreationEnd(appCreationStartTime);
   761→              }
   762→
   763→              const deployStartTime = Instrumentation.trackDeploymentStart(
   764→                applicationId!,
   765→              );
   766→
   767→              const deployResult = await writeMemfsToTempDir(
   768→                memfsVolume,
   769→                virtualDir,
   770→              )
   771→                .then((tempDirPath) =>
   772→                  deployApp({
   773→                    appId: applicationId!,
   774→                    appDirectory: tempDirPath,
   775→                    databricksMode: Boolean(requestBody.databricksHost),
   776→                    templateId,
   777→                  }),
   778→                )
   779→                .catch(async (error) => {
   780→                  streamLog(
   781→                    {
   782→                      message: `[appId: ${applicationId}] Error deploying app: ${error}`,
   783→                      applicationId,
   784→                      traceId,
   785→                      userId,
   786→                      error:
   787→                        error instanceof Error ? error.message : String(error),
   788→                    },
   789→                    'error',
   790→                  );
   791→                  pushAndSaveStreamingErrorMessage(
   792→                    session,
   793→                    applicationId!,
   794→                    new StreamingError(
   795→                      `There was an error deploying your application, check the code in the Github repository.`,
   796→                      applicationId!,
   797→                      AgentStatus.RUNNING,
   798→                      traceId!,
   799→                    ),
   800→                  );
   801→
   802→                  Instrumentation.captureError(error, {
   803→                    applicationId: applicationId!,
   804→                    traceId: traceId!,
   805→                    userId,
   806→                    context: 'deploy_app_error',
   807→                  });
   808→                });
   809→
   810→              Instrumentation.trackDeploymentEnd(deployStartTime, 'complete');
   811→
   812→              if (deployResult) {
   813→                streamLog(
   814→                  {
   815→                    message: `[appId: ${applicationId}] adding app URL to github, appURL: ${deployResult.appURL}`,
   816→                    applicationId,
   817→                    traceId,
   818→                    userId,
   819→                  },
   820→                  'info',
   821→                );
   822→
   823→                await addAppURL({
   824→                  githubEntity,
   825→                  appURL: deployResult.appURL,
   826→                });
   827→
   828→                await pushAndSavePlatformMessage(
   829→                  session,
   830→                  applicationId,
   831→                  new PlatformMessage(
   832→                    AgentStatus.IDLE,
   833→                    traceId!,
   834→                    applicationId,
   835→                    `Your application is being deployed:`,
   836→                    {
   837→                      type: PlatformMessageType.DEPLOYMENT_IN_PROGRESS,
   838→                      deploymentId: deployResult.deploymentId,
   839→                      deploymentUrl: deployResult.appURL,
   840→                      deployStatus: DeployStatus.DEPLOYING,
   841→                      githubUrl: githubEntity.repositoryUrl,
   842→                      deploymentType: requestBody.databricksHost
   843→                        ? 'databricks'
   844→                        : 'koyeb',
   845→                    },
   846→                  ),
   847→                  userId,
   848→                );
   849→              }
   850→            }
   851→
   852→            const canBreakStream =
   853→              completeParsedMessage.status === AgentStatus.IDLE &&
   854→              completeParsedMessage.message.kind !==
   855→                MessageKind.REFINEMENT_REQUEST;
   856→            if (canBreakStream) {
   857→              streamLog(
   858→                {
   859→                  message: `[appId: ${applicationId}] stream can break, aborting`,
   860→                  applicationId,
   861→                  traceId,
   862→                  userId,
   863→                },
   864→                'info',
   865→              );
   866→              abortController.abort();
   867→            }
   868→          } catch (error) {
   869→            // this is a special case for incomplete messages
   870→            if (
   871→              error instanceof Error &&
   872→              error.message.includes('Unterminated string')
   873→            ) {
   874→              streamLog(
   875→                {
   876→                  message: `[appId: ${applicationId}] incomplete message`,
   877→                  applicationId,
   878→                  traceId,
   879→                  userId,
   880→                },
   881→                'error',
   882→              );
   883→              return;
   884→            }
   885→
   886→            streamLog(
   887→              {
   888→                message: `[appId: ${applicationId}] Error handling SSE message: ${error}`,
   889→                applicationId,
   890→                traceId,
   891→                userId,
   892→              },
   893→              'error',
   894→            );
   895→          }
   896→        };
   897→
   898→        messageHandlerQueue.enqueue(messageHandler, 'messageHandler');
   899→      },
   900→      onError(err) {
   901→        Instrumentation.captureError(err.origin as Error, {
   902→          applicationId: applicationId || 'unknown',
   903→          traceId: traceId || 'unknown',
   904→          userId,
   905→          context: 'sse_error',
   906→        });
   907→
   908→        streamLog(
   909→          {
   910→            message: `[appId: ${applicationId}] SSE error: ${JSON.stringify(
   911→              err,
   912→            )}`,
   913→            applicationId,
   914→            traceId,
   915→            userId,
   916→          },
   917→          'error',
   918→        );
   919→
   920→        const errorMessage =
   921→          err.origin?.message ||
   922→          err.origin?.code ||
   923→          err.origin?.toString() ||
   924→          `${err.type} error`;
   925→
   926→        // Only try to terminate if session is still connected
   927→        if (session.isConnected) {
   928→          terminateStreamWithError(
   929→            session,
   930→            `There was an error with the stream: ${errorMessage}`,
   931→            abortController,
   932→            traceId as TraceId,
   933→          );
   934→        } else {
   935→          app.log.error(
   936→            `[appId: ${applicationId}] Stream error but session already disconnected: ${errorMessage}`,
   937→          );
   938→          abortController.abort();
   939→        }
   940→
   941→        // Return false to not retry
   942→        return false;
   943→      },
   944→      onClose() {
   945→        streamLog(
   946→          {
   947→            message: `[appId: ${applicationId}] stream closed by agent`,
   948→            applicationId,
   949→            traceId,
   950→            userId,
   951→          },
   952→          'info',
   953→        );
   954→      },
   955→    });
   956→
   957→    await messageHandlerQueue.waitForCompletion(streamLog);
   958→    if (isPermanentApp) conversationManager.removeConversation(applicationId);
   959→    Instrumentation.trackAiAgentEnd(traceId!, 'success');
   960→    Instrumentation.trackSseEvent('sse_connection_ended', {
   961→      applicationId,
   962→      userId,
   963→    });
   964→
   965→    streamLog(
   966→      {
   967→        message: 'Stream finished',
   968→        applicationId,
   969→        traceId,
   970→        userId,
   971→      },
   972→      'info',
   973→    );
   974→    session.push(
   975→      { done: true, traceId: traceId, appId: applicationId },
   976→      'done',
   977→    );
   978→    session.removeAllListeners();
   979→
   980→    reply.raw.end();
   981→  } catch (error) {
   982→    Instrumentation.trackAiAgentEnd(traceId!, 'error');
   983→    Instrumentation.trackSseEvent('sse_connection_error', {
   984→      error: String(error),
   985→      applicationId,
   986→      userId,
   987→    });
   988→
   989→    Instrumentation.captureError(error as Error, {
   990→      applicationId: applicationId || 'unknown',
   991→      traceId: traceId || 'unknown',
   992→      userId,
   993→      context: 'post_message_main',
   994→    });
   995→
   996→    streamLog(
   997→      {
   998→        message: 'Unhandled error',
   999→        applicationId,
  1000→        traceId,
  1001→        userId,
  1002→        error: String(error),
  1003→      },
  1004→      'error',
  1005→    );
  1006→    session.push(
  1007→      new StreamingError(
  1008→        (error as Error).message ?? 'Unknown error',
  1009→        applicationId!,
  1010→        AgentStatus.IDLE,
  1011→        traceId,
  1012→      ),
  1013→      'error',
  1014→    );
  1015→    session.removeAllListeners();
  1016→    return reply.status(500).send({
  1017→      applicationId,
  1018→      error: `An error occurred while processing your request: ${error}`,
  1019→      status: 'error',
  1020→      traceId,
  1021→    });
  1022→  }
  1023→}
