  1045→async function appCreation({
  1046→  applicationId,
  1047→  appName,
  1048→  techStack,
  1049→  traceId,
  1050→  agentState,
  1051→  githubEntity,
  1052→  ownerId,
  1053→  session,
  1054→  requestBody,
  1055→  files,
  1056→  streamLog,
  1057→}: {
  1058→  applicationId: string;
  1059→  appName: string;
  1060→  techStack: TemplateId;
  1061→  traceId: TraceId;
  1062→  agentState: AgentSseEvent['message']['agentState'];
  1063→  githubEntity: GithubEntityInitialized;
  1064→  ownerId: string;
  1065→  session: Session;
  1066→  requestBody: RequestBody;
  1067→  files: ReturnType<typeof readDirectoryRecursive>;
  1068→  streamLog: StreamLogFunction;
  1069→}) {
  1070→  if (isDev) {
  1071→    fs.writeFileSync(
  1072→      `${logsFolder}/${applicationId}-files.json`,
  1073→      JSON.stringify(files, null, 2),
  1074→    );
  1075→  }
  1076→
  1077→  streamLog(
  1078→    {
  1079→      message: 'Creating app with name',
  1080→      appName,
  1081→      applicationId,
  1082→      traceId,
  1083→    },
  1084→    'info',
  1085→  );
  1086→
  1087→  // Use the isolated createApp function with the existing applicationId
  1088→  const result = await createApp({
  1089→    applicationId, // Pass the existing applicationId
  1090→    appName,
  1091→    githubEntity,
  1092→    ownerId,
  1093→    clientSource: requestBody.clientSource,
  1094→    traceId,
  1095→    agentState,
  1096→    databricksApiKey: requestBody.databricksApiKey,
  1097→    databricksHost: requestBody.databricksHost,
  1098→    files,
  1099→    techStack,
  1100→  });
  1101→
  1102→  streamLog(
  1103→    {
  1104→      message: 'App created',
  1105→      applicationId,
  1106→      traceId,
  1107→    },
  1108→    'info',
  1109→  );
  1110→
  1111→  const inMemoryMessages =
  1112→    conversationManager.getConversationHistory(applicationId);
  1113→
  1114→  await saveMessageToDB(
  1115→    inMemoryMessages.map((message) => ({
  1116→      appId: applicationId,
  1117→      message: message.content,
  1118→      role: message.role,
  1119→      messageKind:
  1120→        message.kind ||
  1121→        (message.role === PromptKind.USER
  1122→          ? MessageKind.USER_MESSAGE
  1123→          : MessageKind.STAGE_RESULT),
  1124→    })),
  1125→  );
  1126→
  1127→  await pushAndSavePlatformMessage(
  1128→    session,
  1129→    applicationId,
  1130→    new PlatformMessage(
  1131→      AgentStatus.IDLE,
  1132→      traceId as TraceId,
  1133→      applicationId,
  1134→      `Your application has been uploaded to GitHub:`,
  1135→      {
  1136→        type: PlatformMessageType.REPO_CREATED,
  1137→        githubUrl: result.repositoryUrl,
  1138→      },
  1139→    ),
  1140→    ownerId,
  1141→  );
  1142→
  1143→  return { newAppName: result.appName };
  1144→}
