  1208→function getExistingConversationBody({
  1209→  existingConversation,
  1210→  userMessage,
  1211→  settings,
  1212→  existingTraceId,
  1213→  applicationId,
  1214→  templateId,
  1215→}: {
  1216→  existingConversation: ConversationData;
  1217→  existingTraceId: string;
  1218→  applicationId: string;
  1219→  userMessage: string;
  1220→  settings?: Record<string, any>;
  1221→  templateId: TemplateId;
  1222→}) {
  1223→  const messages = existingConversation.allMessages;
  1224→
  1225→  return {
  1226→    allMessages: [
  1227→      ...messages,
  1228→      {
  1229→        role: PromptKind.USER,
  1230→        content: userMessage,
  1231→      },
  1232→    ],
  1233→    agentState: existingConversation.agentState,
  1234→    traceId: existingTraceId,
  1235→    applicationId,
  1236→    settings: settings || {},
  1237→    templateId,
  1238→  };
  1239→}
  1240→
  1241→function createStreamLogger(
  1242→  session: Session,
  1243→  isPrivilegedUser: boolean,
  1244→): StreamLogFunction {
  1245→  return function streamLog(
  1246→    logData: StructuredLog,
  1247→    level: 'info' | 'error' = 'info',
  1248→  ) {
  1249→    app.log[level](logData);
  1250→
  1251→    // only push if is privileged user
  1252→    if (isPrivilegedUser) {
  1253→      session.push(
  1254→        { log: logData.message, level, appId: logData.applicationId },
  1255→        'debug',
  1256→      );
  1257→    }
  1258→  };
  1259→}
  1260→
  1261→async function getMessagesFromHistory(
  1262→  applicationId: string,
  1263→  userId: string,
  1264→): Promise<ConversationMessage[]> {
  1265→  if (conversationManager.hasConversation(applicationId)) {
  1266→    // for temp apps, first check in-memory
  1267→    const memoryMessages =
  1268→      conversationManager.getConversationHistory(applicationId);
  1269→    if (memoryMessages.length > 0) {
  1270→      return memoryMessages;
  1271→    }
  1272→    // fallback for corner cases
  1273→    return await getMessagesFromDB(applicationId, userId);
  1274→  }
  1275→
  1276→  // for permanent apps, fetch from db
  1277→  return await getMessagesFromDB(applicationId, userId);
  1278→}
  1279→
  1280→async function getMessagesFromDB(
  1281→  applicationId: string,
  1282→  userId: string,
  1283→): Promise<ConversationMessage[]> {
  1284→  const history = await getAppPromptHistoryForAgent(applicationId, userId);
  1285→
  1286→  if (!history || history.length === 0) {
  1287→    return [];
  1288→  }
  1289→
  1290→  return history
  1291→    .filter((prompt) => {
  1292→      // platform messages should not go to agent
  1293→      return prompt.messageKind !== MessageKind.PLATFORM_MESSAGE;
  1294→    })
  1295→    .map((prompt) => {
  1296→      if (prompt.kind === PromptKind.USER) {
  1297→        return {
  1298→          role: PromptKind.USER,
  1299→          content: prompt.prompt,
  1300→          kind: MessageKind.USER_MESSAGE,
  1301→        };
  1302→      }
  1303→      return {
  1304→        role: PromptKind.ASSISTANT,
  1305→        content: prompt.prompt,
  1306→        kind: (prompt.messageKind || MessageKind.STAGE_RESULT) as MessageKind,
  1307→        metadata: prompt.metadata,
  1308→      };
  1309→    });
  1310→}
  1311→
  1312→async function saveMessageToDB(message: DBMessage[]): Promise<void>;
  1313→async function saveMessageToDB(message: DBMessage): Promise<void>;
  1314→async function saveMessageToDB(
  1315→  message: DBMessage | DBMessage[],
  1316→): Promise<void> {
  1317→  const messageArray = Array.isArray(message) ? message : [message];
  1318→  const values = messageArray.map((message) => ({
  1319→    id: uuidv4(),
  1320→    prompt: message.message,
  1321→    appId: message.appId,
  1322→    kind: message.role,
  1323→    messageKind: message.messageKind,
  1324→    metadata: message.metadata,
  1325→  }));
  1326→
  1327→  try {
  1328→    await db.insert(appPrompts).values(values);
  1329→  } catch (error) {
  1330→    app.log.error({
  1331→      message: 'Error saving message to DB',
  1332→      error: error instanceof Error ? error.message : String(error),
  1333→      appId: messageArray[0]?.appId,
  1334→      role: messageArray[0]?.role,
  1335→      messageKind: messageArray[0]?.messageKind,
  1336→    });
  1337→    throw error;
  1338→  }
  1339→}
  1340→
  1341→async function pushAndSavePlatformMessage(
  1342→  session: Session,
  1343→  applicationId: string,
  1344→  message: PlatformMessage,
  1345→  userId: string,
  1346→) {
  1347→  if (message.metadata?.type) {
  1348→    const messageType = message.metadata.type;
  1349→    Instrumentation.trackPlatformMessage(messageType, userId);
  1350→  }
  1351→
  1352→  session.push(message);
  1353→
  1354→  const messageContent = message.message.messages[0]?.content || '';
  1355→  await saveMessageToDB({
  1356→    appId: applicationId,
  1357→    message: messageContent,
  1358→    role: PromptKind.ASSISTANT,
  1359→    messageKind: MessageKind.PLATFORM_MESSAGE,
  1360→    metadata: message.metadata,
  1361→  });
  1362→}
  1363→
  1364→async function pushAndSaveStreamingErrorMessage(
  1365→  session: Session,
  1366→  applicationId: string,
  1367→  message: StreamingError,
  1368→) {
  1369→  session.push(message);
  1370→
  1371→  const messageContent = message.message.messages[0]?.content || '';
  1372→
  1373→  await saveMessageToDB({
  1374→    appId: applicationId,
  1375→    message: messageContent,
  1376→    role: PromptKind.ASSISTANT,
  1377→    messageKind: MessageKind.RUNTIME_ERROR,
  1378→  });
  1379→}
  1380→
  1381→async function saveAgentMessages(
  1382→  applicationId: string,
  1383→  agentEvent: AgentSseEvent,
  1384→) {
  1385→  const preparedMessages = agentEvent.message.messages.map((message) => ({
  1386→    appId: applicationId,
  1387→    message: message.content,
  1388→    role: message.role,
  1389→    messageKind: agentEvent.message.kind,
  1390→  }));
  1391→
  1392→  // save each message from the agent event
  1393→  await saveMessageToDB(preparedMessages);
  1394→}
  1395→
  1396→function terminateStreamWithError(
  1397→  session: Session,
  1398→  error: string,
  1399→  abortController: AbortController,
  1400→  traceId: TraceId,
  1401→) {
  1402→  const appId = extractApplicationIdFromTraceId(traceId);
  1403→  if (!appId) {
  1404→    throw new Error('App ID not found in trace ID');
  1405→  }
  1406→
  1407→  session.push(
  1408→    new StreamingError(error, appId, AgentStatus.IDLE, traceId),
  1409→    'error',
  1410→  );
  1411→  abortController.abort();
  1412→  session.removeAllListeners();
  1413→}
