   372→    if (error.includes('already exists')) {
   373→      const orgDeployments = await db
   374→        .select({
   375→          koyebOrgEcrSecretId: deployments.koyebOrgEcrSecretId,
   376→        })
   377→        .from(deployments)
   378→        .where(and(eq(deployments.koyebOrgId, koyebOrgId)))
   379→        .limit(1);
   380→
   381→      if (!orgDeployments[0]?.koyebOrgEcrSecretId) {
   382→        throw new Error('Secret already exists but no deployments found');
   383→      }
   384→
   385→      return orgDeployments[0].koyebOrgEcrSecretId;
   386→    }
   387→
   388→    logger.error('Failed to create ECR secret', {
   389→      status: response.status,
   390→      statusText: response.statusText,
   391→      error,
   392→    });
   393→    throw new Error(error);
