  1011→function scheduleOrganizationDeletion({
  1012→  orgId,
  1013→  token,
  1014→}: {
  1015→  orgId: string;
  1016→  token: string;
  1017→}): Promise<{ deleted: boolean; alreadyDeleted: boolean }> {
  1018→  let attempt = 0;
  1019→  const maxAttempts = 30;
  1020→  const delay = 5_000;
  1021→
  1022→  async function checkStatusAndScheduleNext(): Promise<{
  1023→    deleted: boolean;
  1024→    alreadyDeleted: boolean;
  1025→  }> {
  1026→    attempt++;
  1027→
  1028→    return getKoyebOrganization({ orgId, token })
  1029→      .then(({ organization }) => {
  1030→        logger.info('Organization status check (scheduled)', {
  1031→          orgId,
  1032→          attempt,
  1033→          status: organization.status,
  1034→          maxAttempts,
  1035→        });
  1036→
  1037→        if (organization.status === 'DEACTIVATED') {
  1038→          logger.info('Organization deactivated, proceeding with deletion', {
  1039→            orgId,
  1040→          });
  1041→          return performOrganizationDeletion({ orgId, token });
  1042→        }
  1043→
  1044→        if (attempt >= maxAttempts) {
  1045→          const error = `Failed to confirm organization deactivation after ${maxAttempts} attempts`;
