     1→import type { FastifyRequest, FastifyReply } from 'fastify';
     2→import { and, eq, isNull, desc } from 'drizzle-orm';
     3→import { apps, db, deployments } from '../db';
     4→import { logger } from '../logger';
     5→import {
     6→  getOrganizationToken,
     7→  getDeploymentChecker,
     8→  updateDeploymentMessage,
     9→} from './koyeb';
    10→import { DeployStatus } from '@appdotbuild/core';
    11→
    12→type DeploymentStatusResponse = {
    13→  message: string;
    14→  isDeployed: boolean;
    15→  type: 'HEALTHY' | 'ERROR' | 'STOPPING' | 'DEPLOYMENT_IN_PROGRESS';
    16→  deploymentType: 'databricks' | 'koyeb';
    17→};
    18→
    19→export const getGenericDeploymentEndpoint = async (
    20→  request: FastifyRequest<{
    21→    Params: { id: string };
    22→    Querystring: { messageId?: string };
    23→  }>,
    24→  reply: FastifyReply,
    25→) => {
    26→  try {
    27→    const { id } = request.params;
    28→    const { messageId } = request.query;
    29→
    30→    if (!id) {
    31→      return reply.status(400).send({ message: 'Deployment ID is required' });
    32→    }
    33→
    34→    // Determine deployment type from the deployment ID format
    35→    const deploymentType = id.startsWith('databricks-')
    36→      ? 'databricks'
    37→      : 'koyeb';
    38→
    39→    if (deploymentType === 'databricks') {
    40→      return await handleDatabricksDeploymentStatus(
    41→        id,
    42→        messageId,
    43→        request,
    44→        reply,
    45→      );
    46→    } else {
    47→      return await handleKoyebDeploymentStatus(id, messageId, request, reply);
    48→    }
    49→  } catch (error) {
    50→    logger.error('Error getting generic deployment status', { error });
    51→    return reply.status(500).send({ message: 'Internal server error' });
    52→  }
    53→};
    54→
    55→async function handleDatabricksDeploymentStatus(
    56→  deploymentId: string,
    57→  messageId: string | undefined,
    58→  request: FastifyRequest,
    59→  reply: FastifyReply,
    60→): Promise<void> {
    61→  try {
    62→    // Extract app ID from deployment ID (format: databricks-{appId})
    63→    const appId = deploymentId.replace('databricks-', '');
    64→
    65→    // Get app status from database
    66→    const app = await db
    67→      .select({
    68→        deployStatus: apps.deployStatus,
    69→        appUrl: apps.appUrl,
    70→      })
    71→      .from(apps)
    72→      .where(and(eq(apps.id, appId), eq(apps.ownerId, request.user.id!)))
    73→      .limit(1);
    74→
    75→    if (!app[0]) {
    76→      return reply.status(404).send({ message: 'App not found' });
    77→    }
    78→
    79→    const { deployStatus } = app[0];
    80→
    81→    // Map database status to deployment response
    82→    let response: DeploymentStatusResponse;
    83→
    84→    switch (deployStatus) {
    85→      case DeployStatus.DEPLOYED:
    86→        response = {
    87→          message: 'Databricks app deployed successfully',
