     1→import { exec as execNative } from 'node:child_process';
     2→import { eq } from 'drizzle-orm';
     3→import { apps, db } from '../db';
     4→import { logger } from '../logger';
     5→import { promisify } from 'node:util';
     6→import type { App } from '../db/schema';
     7→import { getOrCreateNeonProject } from './neon';
     8→import fs from 'node:fs';
     9→import { DeployStatus } from '@appdotbuild/core';
    10→import z from 'zod';
    11→
    12→const exec = promisify(execNative);
    13→
    14→const DATABASE_URL_ENV_KEY = 'APP_DATABASE_URL';
    15→const DATABASE_URL_RESOURCE_KEY = 'APP_DATABASE_URL';
    16→
    17→const databricksScopeSchema = z.object({
    18→  key: z.string(),
