    21→
    22→export async function deployToDatabricks({
    23→  appId,
    24→  appDirectory,
    25→  currentApp,
    26→}: {
    27→  appId: string;
    28→  appDirectory: string;
    29→  currentApp: Partial<App>;
    30→}) {
    31→  // Validate appId to prevent shell injection
    32→  validateAppId(appId);
    33→
    34→  if (!currentApp.databricksApiKey || !currentApp.databricksHost) {
    35→    throw new Error(
    36→      'Databricks API key and host are required for Databricks deployment',
    37→    );
    38→  }
    39→
    40→  const { connectionString, neonProjectId } = await getOrCreateNeonProject({
    41→    existingNeonProjectId: currentApp.neonProjectId ?? undefined,
    42→  });
    43→
    44→  // Update app status to deploying for databricks deployments
    45→  await db
    46→    .update(apps)
    47→    .set({
    48→      deployStatus: DeployStatus.DEPLOYING,
    49→      neonProjectId,
    50→    })
    51→    .where(eq(apps.id, appId));
    52→
    53→  // Create isolated environment variables for this deployment
    54→  const databricksEnv = {
    55→    ...process.env,
    56→    DATABRICKS_HOST: currentApp.databricksHost,
    57→    DATABRICKS_TOKEN: currentApp.databricksApiKey,
    58→  };
    59→
    60→  // Generate a unique workspace path for this app
    61→  const shortAppId = appId.slice(0, 8);
    62→  const appName = `app-${shortAppId}`;
    63→
    64→  const workspaceSourceCodePath = `/${appName}`;
    65→
    66→  logger.info('Starting Databricks deployment', {
    67→    appId,
    68→    workspaceSourceCodePath,
    69→    databricksHost: currentApp.databricksHost,
    70→  });
    71→
    72→  try {
    73→    // 1. Check if the app exists
    74→    const appExists = await checkDatabricksAppExists({
    75→      appName,
    76→      databricksHost: currentApp.databricksHost,
    77→      databricksApiKey: currentApp.databricksApiKey,
    78→    });
    79→
    80→    // 2. Create a databricks app IF it doesn't exist
    81→    if (appExists) {
    82→      logger.info(`Databricks app ${appName} already exists`);
    83→    } else {
    84→      logger.info(`Creating Databricks app ${appName}`);
    85→      await exec(`databricks apps create ${appName}`, {
    86→        env: databricksEnv,
    87→      });
    88→    }
    89→
    90→    // 3. Check if the scope exists
    91→    const scopeName = appName;
    92→    const scope = await checkIfScopeAlreadyExists({
    93→      scopeName,
    94→      databricksHost: currentApp.databricksHost,
    95→      databricksApiKey: currentApp.databricksApiKey,
    96→      secretName: DATABASE_URL_ENV_KEY,
    97→    });
    98→
    99→    if (scope) {
   100→      logger.info(`Scope ${scopeName} already exists`);
   101→    } else {
   102→      logger.info(`Scope ${appName} does not exist, creating it`);
   103→      // 3. setup secrets in databricks
   104→      await setupDatabricksAppSecrets({
   105→        scopeName,
   106→        databricksHost: currentApp.databricksHost,
   107→        databricksApiKey: currentApp.databricksApiKey,
   108→        secrets: {
   109→          [DATABASE_URL_ENV_KEY]: connectionString,
   110→        },
   111→      });
   112→    }
   113→
   114→    // 4. Create a databricks config file, so the app can access Databricks secrets
   115→    const databricksConfigFile = createDatabricksConfigFile([
   116→      {
   117→        sectionName: DATABASE_URL_RESOURCE_KEY,
   118→        secretSection: {
   119→          scope: scopeName,
   120→          key: DATABASE_URL_ENV_KEY,
   121→        },
   122→      },
   123→    ]);
   124→    fs.writeFileSync(`${appDirectory}/databricks.yml`, databricksConfigFile);
   125→
   126→    // 5. Create a databricks app file, to define the app's environment variables
   127→    const databricksAppFile = createDatabricksAppFile([
   128→      {
   129→        name: DATABASE_URL_ENV_KEY,
   130→        value: connectionString,
   131→        isSecret: false,
   132→
   133→        // TODO: use this instead, when we figure out how to get the secret value from the scope
   134→        // value: DATABASE_URL_ENV_KEY,
   135→        // isSecret: true,
   136→      },
   137→    ]);
   138→    fs.writeFileSync(`${appDirectory}/app.yaml`, databricksAppFile);
   139→
   140→    // 6. Import the code into the databricks workspace (after all files are created)
   141→    logger.info('Importing code to Databricks workspace', {
   142→      appId,
   143→      workspaceSourceCodePath,
   144→      databricksHost: currentApp.databricksHost,
   145→    });
   146→    await exec(
   147→      `databricks workspace import-dir --overwrite "${appDirectory}" "${workspaceSourceCodePath}"`,
   148→      {
   149→        cwd: appDirectory,
   150→        env: databricksEnv,
   151→      },
   152→    );
   153→
   154→    // 7. Deploy the app from there
   155→    logger.info('Deploying app to Databricks');
   156→    const deployResult = await exec(
   157→      `databricks apps deploy ${appName} --source-code-path /Workspace${workspaceSourceCodePath}`,
   158→      {
   159→        cwd: appDirectory,
   160→        env: databricksEnv,
   161→      },
   162→    );
   163→    logger.info('Databricks deployment completed', {
   164→      appId,
   165→      deployOutput: deployResult.stdout,
   166→    });
   167→
   168→    const appUrl = (
   169→      await exec(`databricks apps get ${appName} | jq -r '.url'`, {
   170→        env: databricksEnv,
   171→      })
   172→    ).stdout.trim();
   173→
   174→    // Update app status to deployed
   175→    await db
   176→      .update(apps)
   177→      .set({
   178→        deployStatus: DeployStatus.DEPLOYED,
   179→        appUrl,
   180→      })
   181→      .where(eq(apps.id, appId));
   182→
   183→    return {
   184→      appURL: appUrl,
   185→      deploymentId: `databricks-${appId}`,
   186→    };
   187→  } catch (error) {
   188→    logger.error('Databricks deployment failed', {
   189→      appId,
   190→      error: error instanceof Error ? error.message : String(error),
   191→    });
   192→
   193→    // Update app status to failed
   194→    await db
   195→      .update(apps)
   196→      .set({
   197→        deployStatus: DeployStatus.FAILED,
   198→      })
   199→      .where(eq(apps.id, appId));
   200→
   201→    throw new Error(
   202→      `Databricks deployment failed: ${
   203→        error instanceof Error ? error.message : String(error)
   204→      }`,
   205→    );
   206→  }
   207→}
