   209→async function checkDatabricksAppExists({
   210→  appName,
   211→  databricksHost,
   212→  databricksApiKey,
   213→}: {
   214→  appName: string;
   215→  databricksHost: string;
   216→  databricksApiKey: string;
   217→}) {
   218→  try {
   219→    const result = await exec(
   220→      `databricks apps list | grep ${appName} || true`,
   221→      {
   222→        env: {
   223→          ...process.env,
   224→          DATABRICKS_HOST: databricksHost,
   225→          DATABRICKS_TOKEN: databricksApiKey,
   226→        },
   227→      },
   228→    );
   229→
   230→    if (result.stderr) {
