    const job = backgroundJobManager.getJobStatus(jobId);

    if (job) {
      let response: DeploymentStatusResponse;

      switch (job.status) {
        case 'running':
          response = {
            message: 'Databricks deployment in progress...',
            isDeployed: false,
            type: 'DEPLOYMENT_IN_PROGRESS',
            deploymentType: 'databricks',
          };
          break;
        case 'completed':
          response = {
            message: 'Databricks app deployed successfully',
            isDeployed: true,
            type: 'HEALTHY',
            deploymentType: 'databricks',
          };
          break;
        case 'failed':
          response = {
            message: `Databricks deployment failed: ${job.error}`,
            isDeployed: false,
            type: 'ERROR',
            deploymentType: 'databricks',
          };
          break;
        default:
          response = {
            message: 'Databricks deployment pending...',
            isDeployed: false,
            type: 'DEPLOYMENT_IN_PROGRESS',
            deploymentType: 'databricks',
          };
      }

      // Update message if deployment is complete
      if (
        messageId &&
