   707→export function getDeploymentChecker({
   708→  deploymentId,
   709→  token,
   710→}: {
   711→  deploymentId: string;
   712→  token: string;
   713→}) {
   714→  async function checkDeployment(): Promise<{
   715→    message: string;
   716→    isDeployed: boolean;
   717→    type: DeploymentState;
   718→  }> {
   719→    try {
   720→      const { koyebDeploymentStatus } = await getKoyebDeployment({
   721→        deploymentId,
   722→        token,
   723→      });
   724→
   725→      logger.info('Deployment status check', {
   726→        deploymentId,
   727→        status: koyebDeploymentStatus,
   728→      });
   729→
   730→      if (koyebDeploymentStatus === 'HEALTHY') {
   731→        logger.info('Deployment is healthy');
   732→        return {
   733→          message: `Your application has been deployed`,
   734→          isDeployed: true,
   735→          type: 'HEALTHY',
   736→        };
   737→      }
   738→
   739→      if (koyebDeploymentStatus === 'STOPPING') {
   740→        logger.info(
   741→          'Deployment is stopping, a new deployment is being created',
   742→        );
   743→        return {
   744→          message: `Current deployment is stopping, a new deployment is being created`,
   745→          isDeployed: false,
   746→          type: 'STOPPING',
   747→        };
   748→      }
   749→
   750→      if (koyebDeploymentStatus === 'ERROR') {
   751→        logger.error('Deployment has error status');
   752→        return {
   753→          message: `There was an error deploying your application.`,
   754→          isDeployed: false,
   755→          type: 'ERROR',
   756→        };
   757→      }
   758→
   759→      logger.info('Deployment still in progress', {
   760→        status: koyebDeploymentStatus,
   761→      });
   762→      return {
   763→        message: `Your application is being deployed`,
   764→        isDeployed: false,
   765→        type: 'DEPLOYMENT_IN_PROGRESS',
   766→      };
   767→    } catch (error) {
   768→      logger.error('Error checking deployment status', { error, deploymentId });
   769→      return {
   770→        message: `Unable to check deployment status`,
   771→        isDeployed: false,
   772→        type: 'ERROR',
   773→      };
   774→    }
   775→  }
   776→
   777→  return checkDeployment;
   778→}
