import type { FastifyRequest, FastifyReply } from 'fastify';
import { and, eq, isNull, desc } from 'drizzle-orm';
import { apps, db, deployments } from '../db';
import { logger } from '../logger';
import {
  getOrganizationToken,
  getDeploymentChecker,
  updateDeploymentMessage,
} from './koyeb';
import { DeployStatus } from '@appdotbuild/core';

type DeploymentStatusResponse = {
  message: string;
  isDeployed: boolean;
  type: 'HEALTHY' | 'ERROR' | 'STOPPING' | 'DEPLOYMENT_IN_PROGRESS';
  deploymentType: 'databricks' | 'koyeb';
};

export const getGenericDeploymentEndpoint = async (
  request: FastifyRequest<{
    Params: { id: string };
    Querystring: { messageId?: string };
  }>,
  reply: FastifyReply,
) => {
  try {
    const { id } = request.params;
    const { messageId } = request.query;

    if (!id) {
      return reply.status(400).send({ message: 'Deployment ID is required' });
    }

    // Determine deployment type from the deployment ID format
    const deploymentType = id.startsWith('databricks-')
      ? 'databricks'
      : 'koyeb';

    if (deploymentType === 'databricks') {
      return await handleDatabricksDeploymentStatus(
        id,
        messageId,
        request,
        reply,
      );
    } else {
      return await handleKoyebDeploymentStatus(id, messageId, request, reply);
    }
  } catch (error) {
    logger.error('Error getting generic deployment status', { error });
    return reply.status(500).send({ message: 'Internal server error' });
  }
};

async function handleDatabricksDeploymentStatus(
  deploymentId: string,
  messageId: string | undefined,
  request: FastifyRequest,
  reply: FastifyReply,
): Promise<void> {
  try {
    // Extract app ID from deployment ID (format: databricks-{appId})
    const appId = deploymentId.replace('databricks-', '');

    // Get app status from database
    const app = await db
      .select({
        deployStatus: apps.deployStatus,
        appUrl: apps.appUrl,
      })
      .from(apps)
      .where(and(eq(apps.id, appId), eq(apps.ownerId, request.user.id!)))
      .limit(1);

    if (!app[0]) {
      return reply.status(404).send({ message: 'App not found' });
    }

    const { deployStatus } = app[0];

    // Map database status to deployment response
    let response: DeploymentStatusResponse;

    switch (deployStatus) {
      case DeployStatus.DEPLOYED:
        response = {
          message: 'Databricks app deployed successfully',
          isDeployed: true,
          type: 'HEALTHY',
          deploymentType: 'databricks',
        };
        break;
      case DeployStatus.FAILED:
        response = {
          message: 'Databricks deployment failed',
          isDeployed: false,
          type: 'ERROR',
          deploymentType: 'databricks',
        };
        break;
      case DeployStatus.DEPLOYING:
        response = {
          message: 'Databricks deployment in progress',
          isDeployed: false,
          type: 'DEPLOYMENT_IN_PROGRESS',
          deploymentType: 'databricks',
        };
        break;
      default:
        response = {
          message: 'Databricks deployment status unknown',
          isDeployed: false,
          type: 'DEPLOYMENT_IN_PROGRESS',
          deploymentType: 'databricks',
        };
    }

    // Update message if deployment is complete
    if (
      messageId &&
      (response.type === 'HEALTHY' || response.type === 'ERROR')
    ) {
      await updateDeploymentMessage(messageId, response.type, response.message);
    }

    return reply.send(response);
  } catch (error) {
    logger.error('Error getting Databricks deployment status', { error });
    return reply.status(500).send({ message: 'Internal server error' });
  }
}

async function handleKoyebDeploymentStatus(
  deploymentId: string,
  messageId: string | undefined,
  request: FastifyRequest,
  reply: FastifyReply,
): Promise<void> {
  try {
    // Use existing Koyeb logic
    const deploymentData = await db
      .select({
        koyebOrgId: deployments.koyebOrgId,
      })
      .from(deployments)
      .where(
        and(
          eq(deployments.ownerId, request.user.id!),
          isNull(deployments.deletedAt),
        ),
      )
      .orderBy(desc(deployments.createdAt))
      .limit(1);

    const koyebOrgId = deploymentData[0]?.koyebOrgId;

    if (!koyebOrgId) {
      return reply
        .status(404)
        .send({ message: 'Organization for user not found' });
    }

    const userToken = await getOrganizationToken(koyebOrgId);

    const deploymentChecker = getDeploymentChecker({
      deploymentId,
      token: userToken,
    });

    const koyebResponse = await deploymentChecker();

    // Add deployment type to the response
    const response: DeploymentStatusResponse = {
      ...koyebResponse,
      deploymentType: 'koyeb',
    };

    // Update message if deployment is complete
    if (
      messageId &&
      (response.type === 'HEALTHY' || response.type === 'ERROR')
    ) {
      await updateDeploymentMessage(messageId, response.type, response.message);
    }

    return reply.send(response);
  } catch (error) {
    logger.error('Error getting Koyeb deployment status', { error });
    return reply.status(500).send({ message: 'Internal server error' });
  }
}

