function getDeploymentType(
  originalType: PlatformMessageType | undefined,
  deploymentStatus: DeploymentStatusData,
  hasPollingError: boolean,
  deploymentType?: 'databricks' | 'koyeb',
): PlatformMessageType {
  // Remove special Databricks handling - let normal polling work
  // if (deploymentType === 'databricks' && originalType === PlatformMessageType.DEPLOYMENT_IN_PROGRESS) {
  //   return originalType;
  // }

