// Add to BackgroundJobManager class
public startCleanupScheduler() {
  // Clean up completed jobs older than 1 hour
  setInterval(() => {
    const oneHourAgo = new Date(Date.now() - 60 * 60 * 1000);
    const jobsToDelete = Array.from(this.jobs.entries())
      .filter(([_, job]) => 
        (job.status === 'completed' || job.status === 'failed') &&
        job.completedAt && job.completedAt < oneHourAgo
      );

    jobsToDelete.forEach(([jobId]) => {
      this.jobs.delete(jobId);
      logger.info('Cleaned up old job', { jobId });
    });
  }, 10 * 60 * 1000); // Run every 10 minutes
}

public getJobMetrics() {
  const jobs = Array.from(this.jobs.values());
  return {
    total: jobs.length,
    pending: jobs.filter(j => j.status === 'pending').length,
    running: jobs.filter(j => j.status === 'running').length, 
    completed: jobs.filter(j => j.status === 'completed').length,
    failed: jobs.filter(j => j.status === 'failed').length,
  };
}
```

