// Add validation before enqueueing job
export async function deployToDatabricksAsync({
  appId,
  appDirectory,
  currentApp,
}: {
  appId: string;
  appDirectory: string;
  currentApp: Partial<App>;
}) {
  // Validate required Databricks configuration
  if (!currentApp.databricksApiKey || !currentApp.databricksHost) {
    throw new Error('Databricks API key and host are required for deployment');
  }

  // Check if deployment is already in progress
  if (currentApp.deployStatus === DeployStatus.DEPLOYING) {
    throw new Error(`Deployment for app ${appId} is already in progress`);
  }

  // Set initial status to DEPLOYING
  await db
    .update(apps)
    .set({ deployStatus: DeployStatus.DEPLOYING })
    .where(eq(apps.id, appId));

  try {
    const jobId = backgroundJobManager.enqueueJob('databricks_deployment', appId, {
      appDirectory,
      currentApp,
    });

    logger.info('Databricks deployment enqueued', { appId, jobId });

    return {
      appURL: null,
      deploymentId: `databricks-${appId}`,
      jobId,
    };
  } catch (error) {
    // Revert status on enqueue failure
    await db
      .update(apps)
      .set({ deployStatus: DeployStatus.FAILED })
      .where(eq(apps.id, appId));

    throw error;
  }
}
```

